   431‚Üí          {currentQuestionIndex === -1 ? (
   432‚Üí            <div className="w-1.5 h-1.5 sm:w-2 sm:h-2 rounded-full bg-white shadow-md" />
   433‚Üí          ) : (
   434‚Üí            allQuestions.slice(Math.max(0, currentQuestionIndex - 2), currentQuestionIndex + 3).map((_, relativeIndex) => {
   435‚Üí              const actualIndex = Math.max(0, currentQuestionIndex - 2) + relativeIndex;
   436‚Üí              return (
   437‚Üí                <div
   438‚Üí                  key={actualIndex}
   439‚Üí                  className={`w-1.5 h-1.5 sm:w-2 sm:h-2 rounded-full transition-all duration-200 ${
   440‚Üí                    actualIndex === currentQuestionIndex
   441‚Üí                      ? 'w-4 sm:w-8 bg-white shadow-md'
   442‚Üí                      : actualIndex < currentQuestionIndex || allQuestions[actualIndex] && responses[allQuestions[actualIndex].category]?.[allQuestions[actualIndex].text]
   443‚Üí                      ? 'bg-white/60'
   444‚Üí                      : 'bg-white/30'
   445‚Üí                  }`}
   446‚Üí                />
   447‚Üí              );
   448‚Üí            })
   449‚Üí          )}
   450‚Üí        </div>
   451‚Üí
   452‚Üí        {currentQuestionIndex === -1 ? (
   453‚Üí          <Button
   454‚Üí            onClick={startTest}
   455‚Üí            size="sm"
   456‚Üí            className="flex-1 max-w-[120px] sm:max-w-[200px] gradient-bg text-white hover:opacity-90 transition-opacity text-xs sm:text-sm"
   457‚Üí          >
   458‚Üí            <span className="hidden xs:inline">Start Test</span>
   459‚Üí            <span className="xs:hidden">Start</span>
   460‚Üí            <ChevronRight className="w-3 h-3 sm:w-4 sm:h-4 ml-1 sm:ml-2" />
   461‚Üí          </Button>
   462‚Üí        ) : currentQuestionIndex === totalQuestions - 1 ? (
   463‚Üí          <Button
   464‚Üí            onClick={handleSubmit}
   465‚Üí            disabled={isSubmitting || getTotalAnswered() < totalQuestions}
   466‚Üí            size="sm"
   467‚Üí            className="flex-1 max-w-[120px] sm:max-w-[200px] gradient-bg text-white hover:opacity-90 transition-opacity text-xs sm:text-sm"
   468‚Üí          >
   469‚Üí            {isSubmitting ? (
   470‚Üí              <>
   471‚Üí                <span className="animate-spin mr-1 sm:mr-2 h-3 w-3 sm:h-4 sm:w-4 border-t-2 border-b-2 border-white rounded-full"></span>
   472‚Üí                <span className="hidden xs:inline">Processing...</span>
