   491‚Üí            <span className="xs:hidden">Next</span>
   492‚Üí            <ChevronRight className="w-3 h-3 sm:w-4 sm:h-4 ml-1 sm:ml-2" />
   493‚Üí          </Button>
   494‚Üí        )}
   495‚Üí      </div>
   496‚Üí      </div>
   497‚Üí      
   498‚Üí      {/* Bottom spacer */}
   499‚Üí      <div className="flex-1" />
   500‚Üí    </div>
   501‚Üí  );
   502‚Üí};
   503‚Üí
   504‚Üíexport { AssessmentForm };

