import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { calculateJobSatisfaction } from '../utils/scoreCalculation';
import { questions } from '../data/questions';
import type { Question } from '../data/questions';
import { getStorageItem, setStorageItem, removeStorageItem, STORAGE_KEYS } from '../utils/storageUtils';
import { setCrossDeviceStorageItem } from '../utils/crossDeviceStorage';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { useToast } from "@/components/ui/use-toast";
import { useAssessmentData } from '@/hooks/useAssessmentData';
import { ChevronLeft, ChevronRight, Info, Check } from 'lucide-react';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';

// Types
export interface QuestionResponse {
  questionId: string;
  response: number;
}

export interface CategoryResponses {
  [questionId: string]: QuestionResponse;
}

export interface AllResponses {
  [category: string]: CategoryResponses;
}

export interface CategoryScore {
  score: number;
  importance?: number;
}

export interface AllCategoryScores {
  [category: string]: CategoryScore;
}

export interface Weights {
  [category: string]: number;
}

const getSatisfactionLabel = (value: number) => {
  const labels = ['Strongly Disagree', 'Disagree', 'Neutral', 'Agree', 'Strongly Agree'];
  return labels[value - 1];
};

const getCategoryLabel = (category: string) => {
  const categoryLabels: Record<string, string> = {
    purpose: 'Meaningful Work',
    growth: 'Professional Growth',
    compensation: 'Fair Compensation',
    balance: 'Work-Life Balance',
    support: 'Team Support',
    culture: 'Company Culture',
    resources: 'Resources & Tools',
    recognition: 'Recognition',
    security: 'Job Security',
    autonomy: 'Work Autonomy'
  };
  return categoryLabels[category] || category;
};

const AssessmentForm: React.FC = () => {
  const [responses, setResponses] = useState<AllResponses>({});
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(-1); // Start with intro card (-1)
  const [isSubmitting, setIsSubmitting] = useState<boolean>(false);
  const [hasShownIntro, setHasShownIntro] = useState(false);
  const navigate = useNavigate();
  const { toast } = useToast();
  const { saveAssessmentData } = useAssessmentData();

  // Get all questions
  const allQuestions: Question[] = [];
  Object.entries(questions).forEach(([category, categoryQuestions]) => {
    if (categoryQuestions) {
      allQuestions.push(...categoryQuestions);
    }
  });

  const totalQuestions = allQuestions.length;
  const currentQuestion = currentQuestionIndex >= 0 ? allQuestions[currentQuestionIndex] : null;

  // Show intro message on first load
  useEffect(() => {
    if (!hasShownIntro) {
      toast({
        title: "Part 2: Your Current Job",
        description: "Now rate how satisfied you are with each aspect in your CURRENT job. We'll calculate your personalized score based on what matters to you.",
        duration: 6000,
      });
      setHasShownIntro(true);
    }
  }, [hasShownIntro, toast]);

  // Show progress milestones
  useEffect(() => {
    const answered = getTotalAnswered();
    if (answered === Math.floor(totalQuestions / 2) && answered < totalQuestions) {
      toast({
        title: "You're doing great!",
        description: `Halfway through! Just ${totalQuestions - answered} more questions to go.`,
        duration: 3000,
      });
    }
  }, [currentQuestionIndex, totalQuestions]);

  // Get current response
  const getCurrentResponse = () => {
    if (!currentQuestion) return undefined;
    const categoryResponses = responses[currentQuestion.category];
    return categoryResponses?.[currentQuestion.text]?.response;
  };

  // Handle answer
  const handleAnswer = useCallback((response: number) => {
    if (!currentQuestion || currentQuestionIndex < 0) return;

    setResponses(prev => ({
      ...prev,
      [currentQuestion.category]: {
        ...prev[currentQuestion.category],
        [currentQuestion.text]: {
          questionId: currentQuestion.text,
          response,
        },
      },
    }));
  }, [currentQuestion, currentQuestionIndex]);

  const startTest = () => {
    // Clear save dialog flag for new assessment
    removeStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN);
    setCurrentQuestionIndex(0);
  };

  const goToPrevious = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    } else if (currentQuestionIndex === 0) {
      setCurrentQuestionIndex(-1); // Go back to intro
    }
  };

  const goToNext = () => {
    if (currentQuestionIndex === -1) {
      startTest();
      return;
    }

    const currentResponse = getCurrentResponse();
    if (currentResponse === undefined) {
      toast({
        title: "Please rate this statement",
        description: "Select a rating before moving to the next question",
        variant: "destructive"
      });
      return;
    }

    if (currentQuestionIndex < totalQuestions - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  // Find first unanswered question
  const findFirstUnansweredQuestion = useCallback(() => {
    for (let i = 0; i < allQuestions.length; i++) {
      const question = allQuestions[i];
      const categoryResponses = responses[question.category];
      const questionResponse = categoryResponses?.[question.text];

      if (!questionResponse || questionResponse.response === undefined) {
        return i;
      }
    }
    return -1;
  }, [responses, allQuestions]);

  // Handle form submission
  const handleSubmit = useCallback(async () => {
    const unansweredIndex = findFirstUnansweredQuestion();
    if (unansweredIndex !== -1) {
      setCurrentQuestionIndex(unansweredIndex);
      toast({
        title: "Incomplete Assessment",
        description: `Please answer all questions. Going to question ${unansweredIndex + 1}`,
        variant: "destructive"
      });
      return;
    }

    setIsSubmitting(true);

    try {
      setStorageItem(STORAGE_KEYS.SATISFACTION_RESPONSES, responses);
      await setCrossDeviceStorageItem(STORAGE_KEYS.SATISFACTION_RESPONSES, responses);

      const satisfactionRatings: Record<string, number> = {};
      Object.entries(responses).forEach(([category, categoryResponses]) => {
        const totalScore = Object.values(categoryResponses).reduce((sum, response) => sum + response.response, 0);
        const avgScore = totalScore / Object.values(categoryResponses).length;
        satisfactionRatings[category] = avgScore;
      });

      const wasImportanceSkipped = getStorageItem(STORAGE_KEYS.IMPORTANCE_SKIPPED, false);
      let importanceAnswers: { category: string; importance: number; weight: number }[] | null = null;
      if (!wasImportanceSkipped) {
        importanceAnswers = getStorageItem(STORAGE_KEYS.IMPORTANCE_RESULTS, null);
      }

      const results = calculateJobSatisfaction(
        responses,
        importanceAnswers
      );

      // Save to both regular and cross-device storage for maximum compatibility
      setStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, results);
      await setCrossDeviceStorageItem(STORAGE_KEYS.JOB_SATISFACTION_RESULTS, results);

      // Clear the save dialog flag so it can be shown for these new results
      setStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN, false);
      await setCrossDeviceStorageItem(STORAGE_KEYS.SAVE_DIALOG_SHOWN, false);

      await saveAssessmentData({
        satisfactionRatings,
        jobSatisfactionResults: results,
        step: 'completed'
      });

      toast({
        title: "Assessment Complete!",
        description: "Redirecting to your detailed results...",
      });

      navigate('/results');
    } catch (error) {
      console.error("Error saving assessment data:", error);
      toast({
        title: "Error Saving Data",
        description: "There was an error saving your assessment. Your progress has been saved locally.",
        variant: "destructive"
      });

      navigate('/results');
    } finally {
      setIsSubmitting(false);
    }
  }, [responses, findFirstUnansweredQuestion, navigate, toast, saveAssessmentData]);

  const getTotalAnswered = () => {
    return Object.values(responses).reduce((acc, categoryResponses) => {
      return acc + Object.keys(categoryResponses).length;
    }, 0);
  };

  if (!currentQuestion && currentQuestionIndex >= 0) {
    return <div>Loading...</div>;
  }

  return (
    <div className="max-w-4xl mx-auto px-4 py-2 sm:py-4 h-screen flex flex-col overflow-hidden">
      {/* Top spacer - slightly reduced for header compensation */}
      <div className="flex-[0.8]" />

      {/* Main content container */}
      <div className="flex-shrink-0 flex-grow-0">
      {/* Progress section - compact */}
      <div className="mb-3 sm:mb-4">
        <div className="flex justify-between items-center mb-2">
          <div className="flex items-center gap-1 sm:gap-2">
            <h2 className="text-sm sm:text-lg font-semibold text-white drop-shadow-lg">
              Part 2: Rate Your Current Job
            </h2>
            <TooltipProvider>
              <Tooltip>
                <TooltipTrigger>
                  <Info className="h-4 w-4 text-white/70 hover:text-white cursor-help" />
                </TooltipTrigger>
                <TooltipContent className="max-w-xs">
                  <p className="text-sm">
                    Rate your satisfaction with each aspect in your CURRENT job. 
                    Your answers will be weighted by what you said matters most to you.
                  </p>
                </TooltipContent>
              </Tooltip>
            </TooltipProvider>
          </div>
          <span className="text-xs sm:text-sm text-white/80 drop-shadow">
            {currentQuestionIndex === -1 ? 'Intro' : `${currentQuestionIndex + 1}/${totalQuestions}`}
          </span>
        </div>
        <div className="w-full bg-white/20 backdrop-blur rounded-full h-1.5 sm:h-2">
          <div 
            className="bg-white/80 h-1.5 sm:h-2 rounded-full transition-all duration-300"
            style={{ width: `${currentQuestionIndex === -1 ? 0 : ((currentQuestionIndex + 1) / totalQuestions) * 100}%` }}
          />
        </div>
      </div>

      {/* Card content - intro or question */}
      <Card 
        className="backdrop-blur-xl border-white/20 shadow-2xl mb-3 sm:mb-4"
        style={{
          backgroundColor: 'rgba(255, 255, 255, 0.8)',
          backdropFilter: 'blur(20px)'
        }}
      >
        {currentQuestionIndex === -1 ? (
          // Intro Card
          <>
            <CardHeader className="pb-3 sm:pb-4">
              <div className="flex items-center gap-2 sm:gap-3">
                <div className="w-8 h-8 sm:w-12 sm:h-12 rounded-full bg-primary/10 flex items-center justify-center">
                  <span className="text-lg sm:text-2xl">üìä</span>
                </div>
                <div>
                  <CardTitle className="text-lg sm:text-2xl font-bold text-gray-900">Job Satisfaction Assessment</CardTitle>
                  <CardDescription className="text-gray-600 mt-1 text-xs sm:text-base">
                    PHASE 2
                  </CardDescription>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="space-y-6">
                <div>
                  <h3 className="text-lg sm:text-xl font-semibold text-gray-900 mb-2 sm:mb-3">Rate Your Current Job</h3>
                  <p className="text-sm sm:text-base text-gray-700 leading-relaxed">
                    Now let's evaluate your current work situation. Rate each statement based on how well it describes your current job, from 1 (Strongly Disagree) to 5 (Strongly Agree). This will help us understand how your current role aligns with your values.
                  </p>
                </div>
                <div className="bg-green-50 border border-green-200 rounded-lg p-3 sm:p-4">
                  <p className="text-xs sm:text-sm text-green-800 font-medium">
                    üí° Complete all questions to continue
                  </p>
                  <p className="text-xs text-green-600 mt-1">
                    Phase 2
                  </p>
                </div>
              </div>
            </CardContent>
          </>
        ) : (
          // Question Card
          <>
            <CardHeader className="pb-2 sm:pb-3">
              <div className="flex items-start justify-between gap-2">
                <CardTitle className="text-base sm:text-xl font-semibold text-gray-900 flex-1">
                  {currentQuestion ? getCategoryLabel(currentQuestion.category) : ''}
                </CardTitle>
                {getCurrentResponse() !== undefined && (
                  <Badge variant="secondary" className="bg-primary/10 text-primary text-xs sm:text-sm flex-shrink-0">
                    {getSatisfactionLabel(getCurrentResponse()!)}
                  </Badge>
                )}
              </div>
              <CardDescription className="text-xs sm:text-base text-gray-600">
                Question {currentQuestionIndex + 1}
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-3 sm:space-y-4">
                <p className="text-sm sm:text-lg md:text-xl text-gray-800 font-semibold leading-relaxed">{currentQuestion?.text}</p>

                <div className="space-y-2 sm:space-y-3">
                  <div className="flex justify-between text-xs sm:text-sm font-medium text-gray-600">
                    <span>Strongly Disagree</span>
                    <span>Strongly Agree</span>
                  </div>

                  <div className="flex justify-center gap-2 sm:gap-3 md:gap-4">
                    {[1, 2, 3, 4, 5].map((value) => (
                      <TooltipProvider key={value}>
                        <Tooltip>
                          <TooltipTrigger asChild>
                            <button
                              type="button"
                              onClick={() => handleAnswer(value)}
                              className={`relative w-12 h-12 sm:w-14 sm:h-14 md:w-16 md:h-16 rounded-full transition-all duration-200 font-semibold text-base sm:text-lg flex items-center justify-center ${
                                getCurrentResponse() === value
                                  ? 'bg-gradient-to-br from-orange-400 via-pink-500 to-pink-600 text-white shadow-lg transform scale-110'
                                  : 'bg-gray-100 hover:bg-gray-200 text-gray-700 hover:scale-105'
                              }`}
                              aria-label={`Rate ${value} out of 5 - ${getSatisfactionLabel(value)}`}
                            >
                              {getCurrentResponse() === value ? (
                                <Check className="w-6 h-6 sm:w-7 sm:h-7 md:w-8 md:h-8 text-white" strokeWidth={3} />
                              ) : (
                                <span>{value}</span>
                              )}
                            </button>
                          </TooltipTrigger>
                          <TooltipContent>
                            <p className="text-sm font-medium">{getSatisfactionLabel(value)}</p>
                          </TooltipContent>
                        </Tooltip>
                      </TooltipProvider>
                    ))}
                  </div>

                  {getCurrentResponse() !== undefined && (
                    <div className="text-center">
                      <p className="text-xs sm:text-sm text-gray-600">
                        You rated this: <span className="font-semibold text-primary">{getSatisfactionLabel(getCurrentResponse()!)}</span>
                      </p>
                    </div>
                  )}
                </div>
              </div>
            </CardContent>
          </>
        )}
      </Card>

      {/* Navigation section - compact */}
      <div className="flex items-center justify-between gap-2 sm:gap-4">
        <Button
          onClick={goToPrevious}
          disabled={currentQuestionIndex === -1}
          variant="secondary"
          size="sm"
          className="flex-1 max-w-[120px] sm:max-w-[200px] bg-white/90 hover:bg-white text-gray-800 border-0 shadow-md hover:shadow-lg text-xs sm:text-sm"
        >
          <ChevronLeft className="w-3 h-3 sm:w-4 sm:h-4 mr-1 sm:mr-2" />
          <span className="hidden xs:inline">Previous</span>
          <span className="xs:hidden">Prev</span>
        </Button>

        <div className="flex items-center gap-1 sm:gap-2 px-2 overflow-hidden">
          {currentQuestionIndex === -1 ? (
            <div className="w-1.5 h-1.5 sm:w-2 sm:h-2 rounded-full bg-white shadow-md" />
          ) : (
            allQuestions.slice(Math.max(0, currentQuestionIndex - 2), currentQuestionIndex + 3).map((_, relativeIndex) => {
              const actualIndex = Math.max(0, currentQuestionIndex - 2) + relativeIndex;
              return (
                <div
                  key={actualIndex}
                  className={`w-1.5 h-1.5 sm:w-2 sm:h-2 rounded-full transition-all duration-200 ${
                    actualIndex === currentQuestionIndex
                      ? 'w-4 sm:w-8 bg-white shadow-md'
                      : actualIndex < currentQuestionIndex || allQuestions[actualIndex] && responses[allQuestions[actualIndex].category]?.[allQuestions[actualIndex].text]
                      ? 'bg-white/60'
                      : 'bg-white/30'
                  }`}
                />
              );
            })
          )}
        </div>

        {currentQuestionIndex === -1 ? (
          <Button
            onClick={startTest}
            size="sm"
            className="flex-1 max-w-[120px] sm:max-w-[200px] gradient-bg text-white hover:opacity-90 transition-opacity text-xs sm:text-sm"
          >
            <span className="hidden xs:inline">Start Test</span>
            <span className="xs:hidden">Start</span>
            <ChevronRight className="w-3 h-3 sm:w-4 sm:h-4 ml-1 sm:ml-2" />
          </Button>
        ) : currentQuestionIndex === totalQuestions - 1 ? (
          <Button
            onClick={handleSubmit}
            disabled={isSubmitting || getTotalAnswered() < totalQuestions}
            size="sm"
            className="flex-1 max-w-[120px] sm:max-w-[200px] gradient-bg text-white hover:opacity-90 transition-opacity text-xs sm:text-sm"
          >
            {isSubmitting ? (
              <>
                <span className="animate-spin mr-1 sm:mr-2 h-3 w-3 sm:h-4 sm:w-4 border-t-2 border-b-2 border-white rounded-full"></span>
                <span className="hidden xs:inline">Processing...</span>
                <span className="xs:hidden">...</span>
              </>
            ) : (
              <>
                <span className="hidden sm:inline">Complete Assessment</span>
                <span className="sm:hidden">Complete</span>
              </>
            )}
          </Button>
        ) : (
          <Button
            onClick={goToNext}
            disabled={getCurrentResponse() === undefined}
            variant="secondary"
            size="sm"
            className="flex-1 max-w-[120px] sm:max-w-[200px] bg-white/90 hover:bg-white text-gray-800 border-0 shadow-md hover:shadow-lg text-xs sm:text-sm"
          >
            <span className="hidden xs:inline">Next</span>
            <span className="xs:hidden">Next</span>
            <ChevronRight className="w-3 h-3 sm:w-4 sm:h-4 ml-1 sm:ml-2" />
          </Button>
        )}
      </div>
      </div>

      {/* Bottom spacer */}
      <div className="flex-1" />
    </div>
  );
};

export { AssessmentForm };

