    26‚Üíimport { firestore } from "./config";
    27‚Üí
    28‚Üí/**
    29‚Üí * Get a document from Firestore
    30‚Üí * @param collectionName Collection name
    31‚Üí * @param documentId Document ID
    32‚Üí * @returns Promise resolving to document data or null if not found
    33‚Üí */
    34‚Üíexport const getDocument = async <T = DocumentData>(
    35‚Üí  collectionName: string,
    36‚Üí  documentId: string
    37‚Üí): Promise<T | null> => {
    38‚Üí  try {
    39‚Üí    const docRef = doc(firestore, collectionName, documentId);
    40‚Üí    const docSnap = await getDoc(docRef);
    41‚Üí    
    42‚Üí    if (docSnap.exists()) {
    43‚Üí      return { id: docSnap.id, ...docSnap.data() } as T;
    44‚Üí    } else {
    45‚Üí      return null;
    46‚Üí    }
    47‚Üí  } catch (error) {
    48‚Üí    console.error(`Error getting document from ${collectionName}:`, error);
    49‚Üí    throw error;
    50‚Üí  }
    51‚Üí};
    52‚Üí
    53‚Üí/**
    54‚Üí * Get multiple documents from a collection with optional query constraints
    55‚Üí * @param collectionName Collection name
    56‚Üí * @param constraints Optional query constraints (where, orderBy, limit, etc.)
    57‚Üí * @returns Promise resolving to array of document data
    58‚Üí */
    59‚Üíexport const getDocuments = async <T = DocumentData>(
    60‚Üí  collectionName: string,
    61‚Üí  constraints: QueryConstraint[] = []
    62‚Üí): Promise<T[]> => {
    63‚Üí  try {
    64‚Üí    const collectionRef = collection(firestore, collectionName);
    65‚Üí    const q = query(collectionRef, ...constraints);
    66‚Üí    const querySnapshot = await getDocs(q);
    67‚Üí    
    68‚Üí    return querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }) as T);
    69‚Üí  } catch (error) {
    70‚Üí    console.error(`Error getting documents from ${collectionName}:`, error);
    71‚Üí    throw error;
    72‚Üí  }
    73‚Üí};
    74‚Üí
    75‚Üí/**
    76‚Üí * Create or update a document in Firestore
    77‚Üí * @param collectionName Collection name
    78‚Üí * @param documentId Document ID
    79‚Üí * @param data Document data
    80‚Üí * @param merge Whether to merge with existing data (default: true)
    81‚Üí * @returns Promise resolving to void
    82‚Üí */
    83‚Üíexport const setDocument = async <T extends DocumentData>(
    84‚Üí  collectionName: string,
    85‚Üí  documentId: string,
    86‚Üí  data: T,
    87‚Üí  merge = true
    88‚Üí): Promise<void> => {
    89‚Üí  try {
    90‚Üí    const docRef = doc(firestore, collectionName, documentId);
    91‚Üí    
    92‚Üí    // Add timestamps
    93‚Üí    const dataWithTimestamps: T & { updatedAt: FieldValue; createdAt?: FieldValue } = {
    94‚Üí      ...data,
    95‚Üí      updatedAt: serverTimestamp(),
    96‚Üí    };
    97‚Üí    
    98‚Üí    // Add createdAt only if creating a new document or if merge is false
    99‚Üí    if (!merge) {
   100‚Üí      dataWithTimestamps.createdAt = serverTimestamp();
   101‚Üí    }
   102‚Üí    
   103‚Üí    await setDoc(docRef, dataWithTimestamps, { merge });
   104‚Üí  } catch (error) {
   105‚Üí    console.error(`Error setting document in ${collectionName}:`, error);
   106‚Üí    throw error;
   107‚Üí  }
   108‚Üí};
   109‚Üí
   110‚Üí/**
   111‚Üí * Update a document in Firestore
   112‚Üí * @param collectionName Collection name
   113‚Üí * @param documentId Document ID
   114‚Üí * @param data Document data to update
   115‚Üí * @returns Promise resolving to void
   116‚Üí */
   117‚Üíexport const updateDocument = async <T extends DocumentData>(
   118‚Üí  collectionName: string,
   119‚Üí  documentId: string,
   120‚Üí  data: Partial<T>
   121‚Üí): Promise<void> => {
   122‚Üí  try {
   123‚Üí    const docRef = doc(firestore, collectionName, documentId);
   124‚Üí    
   125‚Üí    // Add updatedAt timestamp
   126‚Üí    const dataWithTimestamp = {
   127‚Üí      ...data,
   128‚Üí      updatedAt: serverTimestamp(),
   129‚Üí    };
   130‚Üí    
   131‚Üí    await updateDoc(docRef, dataWithTimestamp);
   132‚Üí  } catch (error) {
   133‚Üí    console.error(`Error updating document in ${collectionName}:`, error);
   134‚Üí    throw error;
   135‚Üí  }
   136‚Üí};
   137‚Üí
   138‚Üí/**
   139‚Üí * Delete a document from Firestore
   140‚Üí * @param collectionName Collection name
   141‚Üí * @param documentId Document ID
   142‚Üí * @returns Promise resolving to void
   143‚Üí */
   144‚Üíexport const deleteDocument = async (
   145‚Üí  collectionName: string,
   146‚Üí  documentId: string
   147‚Üí): Promise<void> => {
   148‚Üí  try {
   149‚Üí    const docRef = doc(firestore, collectionName, documentId);
   150‚Üí    await deleteDoc(docRef);
   151‚Üí  } catch (error) {
   152‚Üí    console.error(`Error deleting document from ${collectionName}:`, error);
   153‚Üí    throw error;
   154‚Üí  }
   155‚Üí};
   156‚Üí
   157‚Üí/**
   158‚Üí * Get documents from a subcollection of a document
   159‚Üí * @param parentCollection Parent collection name
   160‚Üí * @param parentId Parent document ID
   161‚Üí * @param subcollection Subcollection name
   162‚Üí * @param constraints Optional query constraints
   163‚Üí * @returns Promise resolving to array of document data
   164‚Üí */
   165‚Üíexport const getSubcollectionDocuments = async <T = DocumentData>(
   166‚Üí  parentCollection: string,
   167‚Üí  parentId: string,
   168‚Üí  subcollection: string,
   169‚Üí  constraints: QueryConstraint[] = []
   170‚Üí): Promise<T[]> => {
   171‚Üí  try {
   172‚Üí    const subcollectionRef = collection(firestore, parentCollection, parentId, subcollection);
   173‚Üí    const q = query(subcollectionRef, ...constraints);
   174‚Üí    const querySnapshot = await getDocs(q);
   175‚Üí    
   176‚Üí    return querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }) as T);
   177‚Üí  } catch (error) {
   178‚Üí    console.error(`Error getting documents from ${parentCollection}/${parentId}/${subcollection}:`, error);
   179‚Üí    throw error;
   180‚Üí  }
   181‚Üí};
   182‚Üí
   183‚Üí/**
   184‚Üí * Create or update a document in a subcollection
   185‚Üí * @param parentCollection Parent collection name
   186‚Üí * @param parentId Parent document ID
   187‚Üí * @param subcollection Subcollection name
