// Firebase Firestore Service
// This file provides functions for Firestore database operations

import {
  collection,
  doc,
  getDoc,
  getDocs,
  setDoc,
  updateDoc,
  deleteDoc,
  query,
  where,
  orderBy,
  limit,
  DocumentData,
  QueryConstraint,
  serverTimestamp,
  Timestamp,
  DocumentReference,
  DocumentSnapshot,
  QuerySnapshot,
  QueryDocumentSnapshot,
  FieldValue
} from "firebase/firestore";
import { firestore } from "./config";

/**
 * Get a document from Firestore
 * @param collectionName Collection name
 * @param documentId Document ID
 * @returns Promise resolving to document data or null if not found
 */
export const getDocument = async <T = DocumentData>(
  collectionName: string,
  documentId: string
): Promise<T | null> => {
  try {
    const docRef = doc(firestore, collectionName, documentId);
    const docSnap = await getDoc(docRef);

    if (docSnap.exists()) {
      return { id: docSnap.id, ...docSnap.data() } as T;
    } else {
      return null;
    }
  } catch (error) {
    console.error(`Error getting document from ${collectionName}:`, error);
    throw error;
  }
};

/**
 * Get multiple documents from a collection with optional query constraints
 * @param collectionName Collection name
 * @param constraints Optional query constraints (where, orderBy, limit, etc.)
 * @returns Promise resolving to array of document data
 */
export const getDocuments = async <T = DocumentData>(
  collectionName: string,
  constraints: QueryConstraint[] = []
): Promise<T[]> => {
  try {
    const collectionRef = collection(firestore, collectionName);
    const q = query(collectionRef, ...constraints);
    const querySnapshot = await getDocs(q);

    return querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }) as T);
  } catch (error) {
    console.error(`Error getting documents from ${collectionName}:`, error);
    throw error;
  }
};

/**
 * Create or update a document in Firestore
 * @param collectionName Collection name
 * @param documentId Document ID
 * @param data Document data
 * @param merge Whether to merge with existing data (default: true)
 * @returns Promise resolving to void
 */
export const setDocument = async <T extends DocumentData>(
  collectionName: string,
  documentId: string,
  data: T,
  merge = true
): Promise<void> => {
  try {
    const docRef = doc(firestore, collectionName, documentId);

    // Add timestamps
    const dataWithTimestamps: T & { updatedAt: FieldValue; createdAt?: FieldValue } = {
      ...data,
      updatedAt: serverTimestamp(),
    };

    // Add createdAt only if creating a new document or if merge is false
    if (!merge) {
      dataWithTimestamps.createdAt = serverTimestamp();
    }

    await setDoc(docRef, dataWithTimestamps, { merge });
  } catch (error) {
    console.error(`Error setting document in ${collectionName}:`, error);
    throw error;
  }
};

/**
 * Update a document in Firestore
 * @param collectionName Collection name
 * @param documentId Document ID
 * @param data Document data to update
 * @returns Promise resolving to void
 */
export const updateDocument = async <T extends DocumentData>(
  collectionName: string,
  documentId: string,
  data: Partial<T>
): Promise<void> => {
  try {
    const docRef = doc(firestore, collectionName, documentId);

    // Add updatedAt timestamp
    const dataWithTimestamp = {
      ...data,
      updatedAt: serverTimestamp(),
    };

    await updateDoc(docRef, dataWithTimestamp);
  } catch (error) {
    console.error(`Error updating document in ${collectionName}:`, error);
    throw error;
  }
};

/**
 * Delete a document from Firestore
 * @param collectionName Collection name
 * @param documentId Document ID
 * @returns Promise resolving to void
 */
export const deleteDocument = async (
  collectionName: string,
  documentId: string
): Promise<void> => {
  try {
    const docRef = doc(firestore, collectionName, documentId);
    await deleteDoc(docRef);
  } catch (error) {
    console.error(`Error deleting document from ${collectionName}:`, error);
    throw error;
  }
};

/**
 * Get documents from a subcollection of a document
 * @param parentCollection Parent collection name
 * @param parentId Parent document ID
 * @param subcollection Subcollection name
 * @param constraints Optional query constraints
 * @returns Promise resolving to array of document data
 */
export const getSubcollectionDocuments = async <T = DocumentData>(
  parentCollection: string,
  parentId: string,
  subcollection: string,
  constraints: QueryConstraint[] = []
): Promise<T[]> => {
  try {
    const subcollectionRef = collection(firestore, parentCollection, parentId, subcollection);
    const q = query(subcollectionRef, ...constraints);
    const querySnapshot = await getDocs(q);

    return querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }) as T);
  } catch (error) {
    console.error(`Error getting documents from ${parentCollection}/${parentId}/${subcollection}:`, error);
    throw error;
  }
};

/**
 * Create or update a document in a subcollection
 * @param parentCollection Parent collection name
 * @param parentId Parent document ID
 * @param subcollection Subcollection name
 * @param documentId Document ID
 * @param data Document data
 * @param merge Whether to merge with existing data (default: true)
 * @returns Promise resolving to void
 */
export const setSubcollectionDocument = async <T extends DocumentData>(
  parentCollection: string,
  parentId: string,
  subcollection: string,
  documentId: string,
  data: T,
  merge = true
): Promise<void> => {
  try {
    const docRef = doc(firestore, parentCollection, parentId, subcollection, documentId);

    // Add timestamps
    const dataWithTimestamps: T & { updatedAt: FieldValue; createdAt?: FieldValue } = {
      ...data,
      updatedAt: serverTimestamp(),
    };

    // Add createdAt only if creating a new document or if merge is false
    if (!merge) {
      dataWithTimestamps.createdAt = serverTimestamp();
    }

    await setDoc(docRef, dataWithTimestamps, { merge });
  } catch (error) {
    console.error(`Error setting document in ${parentCollection}/${parentId}/${subcollection}:`, error);
    throw error;
  }
};

