import { Timestamp, DocumentReference } from 'firebase/firestore';

/**
 * Base model interface for all Firestore documents
 * Contains common fields that all documents should have
 */
export interface BaseModel {
  id: string;
  createdAt: Timestamp;
  updatedAt: Timestamp;
}

/**
 * User profile model
 * Extends the base model with user-specific fields
 */
export interface UserProfile extends BaseModel {
  uid: string;
  email: string;
  displayName: string;
  photoURL?: string;
  phoneNumber?: string;
  bio?: string;
  preferences?: UserPreferences;
  isActive: boolean;
  lastLoginAt?: Timestamp;
}

/**
 * User preferences model
 * Contains user preference settings
 */
export interface UserPreferences {
  theme: 'light' | 'dark' | 'system';
  emailNotifications: boolean;
  pushNotifications: boolean;
  language: string;
}

/**
 * Todo item model
 * Represents a todo item in the application
 */
export interface TodoItem extends BaseModel {
  title: string;
  description?: string;
  completed: boolean;
  dueDate?: Timestamp;
  priority: 'low' | 'medium' | 'high';
  tags?: string[];
  userId: string;
  assignedTo?: string[];
  category?: string;
  attachments?: Attachment[];
}

/**
 * Attachment model
 * Represents a file attachment
 */
export interface Attachment extends BaseModel {
  fileName: string;
  fileType: string;
  fileSize: number;
  fileURL: string;
  uploadedAt: Timestamp;
  uploadedBy: string;
}

/**
 * Category model
 * Represents a category for organizing todo items
 */
export interface Category extends BaseModel {
  name: string;
  description?: string;
  color?: string;
  userId: string;
  isDefault: boolean;
}

/**
 * Comment model
 * Represents a comment on a todo item
 */
export interface Comment extends BaseModel {
  text: string;
  userId: string;
  todoId: string;
  parentCommentId?: string;
  attachments?: Attachment[];
}

/**
 * Activity log model
 * Represents an activity log entry for tracking changes
 */
export interface ActivityLog extends BaseModel {
  action: 'create' | 'update' | 'delete' | 'complete' | 'assign' | 'comment';
  entityId: string;
  entityType: 'todo' | 'category' | 'comment' | 'user';
  userId: string;
  details?: Record<string, any>;
}

/**
 * Collection names enum
 * Provides type-safe access to collection names
 */
export enum Collections {
  USERS = 'users',
  TODOS = 'todos',
  CATEGORIES = 'categories',
  COMMENTS = 'comments',
  ACTIVITY_LOGS = 'activityLogs',
  ATTACHMENTS = 'attachments',
}

/**
 * Subcollection names enum
 * Provides type-safe access to subcollection names
 */
export enum Subcollections {
  PREFERENCES = 'preferences',
  ATTACHMENTS = 'attachments',
  COMMENTS = 'comments',
}

/**
 * Helper type for creating a new document
 * Omits the id, createdAt, and updatedAt fields which are set automatically
 */
export type CreateModel<T extends BaseModel> = Omit<T, 'id' | 'createdAt' | 'updatedAt'>;

/**
 * Helper type for updating a document
 * Makes all fields optional except id
 */
export type UpdateModel<T extends BaseModel> = Partial<Omit<T, 'id' | 'createdAt'>> & { id: string };

