// Authentication Context Provider
// This file provides a React context for authentication state

import React, { createContext, useContext, ReactNode, useState, useEffect } from 'react';
import { User } from 'firebase/auth';
import { 
  registerUser, 
  loginUser, 
  logoutUser, 
  getCurrentUser,
  subscribeToAuthChanges,
  updateUserProfile,
  resetPassword
} from '@/firebase';

// Define the context type
interface AuthContextType {
  currentUser: User | null;
  loading: boolean;
  error: string | null;
  register: (email: string, password: string) => Promise<void>;
  login: (email: string, password: string) => Promise<void>;
  logout: () => Promise<void>;
  updateProfile: (displayName?: string, photoURL?: string) => Promise<void>;
  sendPasswordReset: (email: string) => Promise<void>;
  clearError: () => void;
}

// Create the context with a default value
const AuthContext = createContext<AuthContextType | undefined>(undefined);

// Provider props type
interface AuthProviderProps {
  children: ReactNode;
}

/**
 * Authentication Provider Component
 * Provides authentication state and functions to the application
 */
export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  // State to track the current user, loading state, and errors
  const [currentUser, setCurrentUser] = useState<User | null>(getCurrentUser());
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  // Subscribe to auth state changes
  useEffect(() => {
    const unsubscribe = subscribeToAuthChanges((user) => {
      setCurrentUser(user);
      setLoading(false);
    });

    // Cleanup subscription on unmount
    return () => unsubscribe();
  }, []);

  // Register a new user
  const register = async (email: string, password: string): Promise<void> => {
    setLoading(true);
    setError(null);
    try {
      await registerUser(email, password);
    } catch (err: any) {
      let errorMessage = "An error occurred during registration";

      // Handle specific Firebase auth errors
      if (err.code === 'auth/email-already-in-use') {
        errorMessage = "This email is already in use";
      } else if (err.code === 'auth/invalid-email') {
        errorMessage = "Invalid email format";
      } else if (err.code === 'auth/weak-password') {
        errorMessage = "Password is too weak";
      } else if (err.code === 'auth/network-request-failed') {
        errorMessage = "Network error. Please check your connection";
      }

      setError(errorMessage);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Login an existing user
  const login = async (email: string, password: string): Promise<void> => {
    setLoading(true);
    setError(null);
    try {
      await loginUser(email, password);

      // Migrate local data to Firestore
      // This will be implemented in the component that uses this context
    } catch (err: any) {
      let errorMessage = "An error occurred during login";

      // Handle specific Firebase auth errors
      if (err.code === 'auth/user-not-found') {
        errorMessage = "No account found with this email";
      } else if (err.code === 'auth/wrong-password') {
        errorMessage = "Incorrect password";
      } else if (err.code === 'auth/invalid-email') {
        errorMessage = "Invalid email format";
      } else if (err.code === 'auth/user-disabled') {
        errorMessage = "This account has been disabled";
      } else if (err.code === 'auth/too-many-requests') {
        errorMessage = "Too many failed login attempts. Please try again later";
      } else if (err.code === 'auth/network-request-failed') {
        errorMessage = "Network error. Please check your connection";
      }

      setError(errorMessage);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Logout the current user
  const logout = async (): Promise<void> => {
    setLoading(true);
    setError(null);
    try {
      await logoutUser();
    } catch (err: any) {
      setError("An error occurred during logout");
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Update user profile
  const updateProfile = async (displayName?: string, photoURL?: string): Promise<void> => {
    setLoading(true);
    setError(null);
    try {
      await updateUserProfile(displayName, photoURL);
    } catch (err: any) {
      setError("An error occurred while updating your profile");
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Send password reset email
  const sendPasswordReset = async (email: string): Promise<void> => {
    setLoading(true);
    setError(null);
    try {
      await resetPassword(email);
    } catch (err: any) {
      let errorMessage = "An error occurred while sending password reset email";

      // Handle specific Firebase auth errors
      if (err.code === 'auth/user-not-found') {
        errorMessage = "No account found with this email";
      } else if (err.code === 'auth/invalid-email') {
        errorMessage = "Invalid email format";
      } else if (err.code === 'auth/network-request-failed') {
        errorMessage = "Network error. Please check your connection";
      }

      setError(errorMessage);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Clear any error
  const clearError = (): void => {
    setError(null);
  };

  // Context value
  const value: AuthContextType = {
    currentUser,
    loading,
    error,
    register,
    login,
    logout,
    updateProfile,
    sendPasswordReset,
    clearError
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

/**
 * Custom hook to use the Auth context
 * @returns Auth context
 * @throws Error if used outside of AuthProvider
 */
export const useAuth = (): AuthContextType => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

