import { Weights } from '../components/AssessmentForm';
import { QuestionResponse, CategoryResponses, AllResponses, CategoryScore, AllCategoryScores } from '../components/AssessmentForm';
import { calculateQuestionScore, calculateFinalScore, clampScore, SCALE_FACTOR, ALPHA } from './calculationCore';
