// Default category weights when importance ratings are skipped
// Updated to match all assessment categories with equal weights
const DEFAULT_CATEGORY_WEIGHTS: Weights = {
  purpose: 0.10,
  growth: 0.10,
  compensation: 0.10,
  balance: 0.10,
  support: 0.10,
  culture: 0.10,
  resources: 0.10,
  recognition: 0.10,
  security: 0.10,
  autonomy: 0.10
};

// Get category names from centralized definitions
const CATEGORY_NAMES: string[] = getCategoryIds();

/**
 * Converts importance ratings to normalized weights that sum to 1.0
 * @param importanceAnswers Array of importance ratings by category
 * @returns Object mapping categories to their normalized weights
 */
export function convertImportanceToWeights(
  importanceAnswers: { category: string; importance: number; weight: number }[] | null
): Weights {
  // If no importance answers, return default weights
  if (!importanceAnswers || importanceAnswers.length === 0) {
    return DEFAULT_CATEGORY_WEIGHTS;
  }

  // Calculate total importance to normalize weights
  const totalImportance = importanceAnswers.reduce(
    (sum, answer) => sum + answer.importance,
    0
  );

  // If total importance is 0 (shouldn't happen), return default weights
  if (totalImportance === 0) {
    return DEFAULT_CATEGORY_WEIGHTS;
  }

  // Create weights object with normalized values
  const weights: Weights = {};
  importanceAnswers.forEach(answer => {
    // Normalize category ID to handle legacy names
    const normalizedCategory = normalizeCategoryId(answer.category);
    weights[normalizedCategory] = answer.importance / totalImportance;
  });

  return weights;
}

export interface JobSatisfactionResult {
  overallScore: number;
  categoryScores: AllCategoryScores;
}

interface ScoreResult {
  totalPoints: number;
  weightedScore: number;
  impactFactor: number;
}

export function calculateJobSatisfaction(
  responses: AllResponses,
  importanceAnswers?: { category: string; importance: number; weight: number }[] | null
): JobSatisfactionResult {
  const isImportanceSkipped = !importanceAnswers;
  const weightedScores: number[] = [];
  const categoryScores: AllCategoryScores = {};

  // Get dynamic weights based on importance ratings or use defaults if skipped
  const categoryWeights = convertImportanceToWeights(importanceAnswers);

  // Process each category
  for (const [category, questions] of Object.entries(responses)) {
    // Normalize category ID to handle legacy names
    const normalizedCategory = normalizeCategoryId(category);
    const categoryQuestionScores: number[] = [];

    // Process each question in the category
    for (const [questionId, response] of Object.entries(questions)) {
      // Get importance rating (default to 3 if not provided)
      const importanceRating = importanceAnswers?.find(
        ans => normalizeCategoryId(ans.category) === normalizedCategory
      )?.importance ?? 3;

      // Calculate score using new logic
      const score = calculateQuestionScore(
        response.response,
        importanceRating,
        0, // No bother level
        isImportanceSkipped
      );

      // Store raw scores for category calculation
      categoryQuestionScores.push(score);
    }

    // Calculate category score (raw, without weight)
    if (categoryQuestionScores.length > 0) {
      const rawCategoryScore = calculateFinalScore(categoryQuestionScores, 'average');

      // Store the importance rating with the category score if available
      const importanceRating = importanceAnswers?.find(
        ans => normalizeCategoryId(ans.category) === normalizedCategory
      )?.importance;

      categoryScores[normalizedCategory] = {
        score: rawCategoryScore,
        ...(importanceRating !== undefined && { importance: importanceRating })
      };

      // Add weighted score for overall calculation
      const categoryWeight = categoryWeights[normalizedCategory];
      if (categoryWeight !== undefined) {
        weightedScores.push(rawCategoryScore * categoryWeight);
      } else {
        // Use a default weight of 0.05 for unknown categories
        weightedScores.push(rawCategoryScore * 0.05);
        console.warn(`Unknown category "${normalizedCategory}" (original: "${category}") found. Using default weight of 0.05.`);
      }
    }
  }

  // Calculate final weighted score
  const finalScore = weightedScores.reduce((acc, score) => acc + score, 0);

  return {
    overallScore: clampScore(finalScore),
    categoryScores
  };
}

// Maintain backward compatibility with the old interface
export function calculateScore(
  satisfactionRating: number,
  importanceRating: number | null
): { totalPoints: number; weightedScore: number; impactFactor: number } {
  const score = calculateQuestionScore(
    satisfactionRating,
    importanceRating ?? 3,
    0, // No bother level
    importanceRating === null
  );

  return {
    totalPoints: score,
    weightedScore: score,
    impactFactor: 1 // No impact factor adjustment
  };
}

