import { useState } from 'react';
import { useAuth } from '@/context';
import { firestore } from '@/firebase';
import { 
  doc, 
  collection, 
  addDoc, 
  getDoc, 
  getDocs, 
  query, 
  orderBy, 
  limit, 
  serverTimestamp 
} from 'firebase/firestore';
import { setCrossDeviceStorageItem, getCrossDeviceStorageItem } from '@/utils/crossDeviceStorage';

interface AssessmentData {
  importanceRatings?: Record<string, number>;
  satisfactionRatings?: Record<string, number>;
  botherLevels?: Record<string, number>;
  satisfactionScore?: number;
  step?: string;
  timestamp?: string;
  [key: string]: any;
}

export const useAssessmentData = () => {
  const { currentUser } = useAuth();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Save assessment data
  const saveAssessmentData = async (data: AssessmentData): Promise<void> => {
    setLoading(true);
    setError(null);

    try {
      // Always save to cross-device storage as a backup (handles private browsing)
      const timestamp = new Date().toISOString();
      const dataWithTimestamp = { ...data, timestamp };
      localStorage.setItem('assessmentData', JSON.stringify(dataWithTimestamp));

      // Also save to cross-device storage for better reliability
      await setCrossDeviceStorageItem('assessmentData', dataWithTimestamp);

      // For authenticated users, save to Firestore
      // We don't await this operation to make the UI more responsive
      if (currentUser) {
        const assessmentRef = collection(firestore, 'users', currentUser.uid, 'assessments');
        // Start the Firestore save operation but don't block on it
        const savePromise = addDoc(assessmentRef, {
          ...data,
          createdAt: serverTimestamp(),
        });

        // Handle any errors from the Firestore save in the background
        savePromise.catch(err => {
          console.error('Background Firestore save error:', err);
          // We don't set the error state here since the function has already "completed" from the user's perspective
        });
      }
    } catch (err) {
      console.error('Error saving assessment data:', err);
      setError('Failed to save assessment data. Please try again.');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Get assessment data
  const getAssessmentData = async (): Promise<AssessmentData | null> => {
    setLoading(true);
    setError(null);

    try {
      // Try to get from local storage first
      const localData = localStorage.getItem('assessmentData');
      let parsedLocalData: AssessmentData | null = null;

      if (localData) {
        parsedLocalData = JSON.parse(localData);
      }

      // For authenticated users, try to get from Firestore
      if (currentUser) {
        try {
          // Get the most recent assessment
          const assessmentsRef = collection(firestore, 'users', currentUser.uid, 'assessments');
          const q = query(assessmentsRef, orderBy('createdAt', 'desc'), limit(1));
          const querySnapshot = await getDocs(q);

          if (!querySnapshot.empty) {
            const firestoreData = querySnapshot.docs[0].data() as AssessmentData;

            // If Firestore data exists, return it
            return firestoreData;
          }
        } catch (firestoreErr) {
          console.error('Error fetching from Firestore:', firestoreErr);
          // Fall back to local data if Firestore fetch fails
        }
      }

      // Return local data if Firestore data doesn't exist or user is not authenticated
      return parsedLocalData;
    } catch (err) {
      console.error('Error getting assessment data:', err);
      setError('Failed to retrieve assessment data.');

      // Fall back to local storage
      const localData = localStorage.getItem('assessmentData');
      return localData ? JSON.parse(localData) : null;
    } finally {
      setLoading(false);
    }
  };

  // Get assessment history (for authenticated users only)
  const getAssessmentHistory = async (): Promise<AssessmentData[]> => {
    setLoading(true);
    setError(null);

    try {
      if (!currentUser) {
        return [];
      }

      const assessmentsRef = collection(firestore, 'users', currentUser.uid, 'assessments');
      const q = query(assessmentsRef, orderBy('createdAt', 'desc'));
      const querySnapshot = await getDocs(q);

      return querySnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      })) as AssessmentData[];
    } catch (err) {
      console.error('Error getting assessment history:', err);
      setError('Failed to retrieve assessment history.');
      return [];
    } finally {
      setLoading(false);
    }
  };

  // Migrate local data to Firestore (useful when a user logs in)
  const migrateLocalDataToFirestore = async (): Promise<void> => {
    if (!currentUser) return;

    setLoading(true);
    setError(null);

    try {
      const localData = localStorage.getItem('assessmentData');
      if (!localData) return;

      const parsedLocalData = JSON.parse(localData);

      // Check if this data already exists in Firestore
      const assessmentsRef = collection(firestore, 'users', currentUser.uid, 'assessments');
      await addDoc(assessmentsRef, {
        ...parsedLocalData,
        migratedFromLocal: true,
        createdAt: serverTimestamp(),
      });
    } catch (err) {
      console.error('Error migrating local data to Firestore:', err);
      setError('Failed to migrate local data to Firestore.');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  return {
    saveAssessmentData,
    getAssessmentData,
    getAssessmentHistory,
    migrateLocalDataToFirestore,
    loading,
    error,
  };
};

