// Define the structure of job satisfaction questions by category

export interface Question {
  text: string;
  category: string;
}

export interface Questions {
  [category: string]: Question[];
}

export const questions: Questions = {
  purpose: [
    { text: "My work gives me a sense of purpose", category: "purpose" },
    { text: "I feel that what I do matters", category: "purpose" },
    { text: "My job aligns with my personal values", category: "purpose" }
  ],
  autonomy: [
    { text: "I have freedom to decide how to do my work", category: "autonomy" },
    { text: "I can make important decisions about my work", category: "autonomy" },
    { text: "I have control over my daily tasks", category: "autonomy" }
  ],
  growth: [
    { text: "I have opportunities to learn and grow", category: "growth" },
    { text: "My job challenges me in a positive way", category: "growth" },
    { text: "I can see a clear career path ahead", category: "growth" }
  ],
  impact: [
    { text: "I can see the positive impact of my work", category: "impact" },
    { text: "My contributions are recognized", category: "impact" },
    { text: "I feel that my work makes a difference", category: "impact" }
  ],
  relationships: [
    { text: "I have positive relationships with colleagues", category: "relationships" },
    { text: "I feel supported by my team", category: "relationships" },
    { text: "I trust the people I work with", category: "relationships" }
  ],
  compensation: [
    { text: "I am fairly compensated for my work", category: "compensation" },
    { text: "My benefits meet my needs", category: "compensation" },
    { text: "My compensation reflects my contribution", category: "compensation" }
  ]
};

/**
 * Job Satisfaction Score Calculation System
 * 
 * This module implements a sophisticated scoring system for job satisfaction that takes into account:
 * 1. Satisfaction Rating (SR): 1-5 scale, with 3 as neutral point
 * 2. Importance Rating (IR): 1-5 scale, optional (can be skipped)
 * 3. Bother Level (BL): 1-10 scale, for negative impact assessment
 * 
 * The system produces scores in the range of -88 to +88, with different calculation modes:
 * 
 * A. When Importance Ratings are Skipped:
 * - Maximum positive score (+88): Achieved with SR = 5 (full satisfaction)
 * - Maximum negative score (-88): Achieved with SR = 1 and BL ‚â• 6
 * - Moderate negative score (-65): Achieved with SR = 1 and BL ‚â§ 5
 * - Neutral score (0): Achieved with SR = 3
 * 
 * B. When Importance Ratings are Used:
 * - Scores are weighted by importance (IR)
 * - Higher IR amplifies both positive and negative scores
 * - IR of 3 provides baseline weighting
 * 
 * Bother Level Impact System:
 * - BL = 10: Deal breaker level (2.0x impact)
 * - BL = 6-9: High bother range (1.37x to 1.85x impact)
 * - BL = 1-5: Standard bother range (1.1x to 1.37x impact)
 * - BL = 0: No additional impact (1.0x)
 * - BL only affects negative satisfaction ratings (SR < 3)
 */

// Constants for calculation
export const SCALE_FACTOR = 88;
export const ALPHA = 0.5;
export const MIN_SCORE = -88;
export const MAX_SCORE = 88;

/**
 * Calculates the weighted impact of a satisfaction rating
 * 
 * @param satisfactionRating - The satisfaction rating (1-5)
 * @param importanceRating - The importance rating (1-5)
 * @param isImportanceSkipped - Whether importance rating was skipped
 * @returns The weighted impact score
 */
export function calculateWeightedImpact(
  satisfactionRating: number,
  importanceRating: number,
  isImportanceSkipped: boolean
): number {
  // Normalize satisfaction rating to range [-1, 1]
  const normalizedSR = (satisfactionRating - 3) / 2;

  // For very low satisfaction (1-2), ensure minimum negative impact
  if (satisfactionRating <= 2) {
    return Math.max(-44, normalizedSR * SCALE_FACTOR);
  }

  // Calculate importance multiplier (1.0 to 2.0)
  let importanceMultiplier = 1.0;
  if (!isImportanceSkipped) {
    // Linear scaling from 1.0 to 2.0 based on importance
    importanceMultiplier = 1.0 + (importanceRating - 1) / 4;
  }

  // Calculate weighted impact
  return normalizedSR * SCALE_FACTOR * importanceMultiplier;
}

/**
 * Calculates the bother level impact for negative satisfaction ratings
 * 
 * @param satisfactionRating - The satisfaction rating (1-5)
 * @param botherLevel - The bother level (0-10)
 * @returns The bother level multiplier
 */
export function calculateBotherLevelImpact(
  satisfactionRating: number,
  botherLevel: number
): number {
  // Bother level is no longer used, always return 1 (no impact)
  return 1;
}

/**
 * Calculates the score for a single question
 * 
 * @param satisfactionRating - The satisfaction rating (1-5)
 * @param importanceRating - The importance rating (1-5)
 * @param botherLevel - The bother level (0-10)
 * @param isImportanceSkipped - Whether importance rating was skipped
 * @returns The calculated score
 */
export function calculateQuestionScore(
  satisfactionRating: number,
  importanceRating: number,
  botherLevel: number,
  isImportanceSkipped: boolean
): number {
  // Calculate weighted impact based on satisfaction and importance
  const weightedImpact = calculateWeightedImpact(
    satisfactionRating,
    importanceRating,
    isImportanceSkipped
  );

  // Return the weighted impact (bother level no longer affects the score)
  // Ensure the score is clamped to valid range
  return clampScore(weightedImpact);
}

/**
 * Calculates the final score from an array of individual scores
 * 
 * @param scores - Array of individual scores
 * @param method - Method to use for calculation ('average', 'sum', etc.)
 * @returns The final calculated score
 */
export function calculateFinalScore(
  scores: number[],
  method: 'average' | 'sum' = 'average'
): number {
  if (scores.length === 0) return 0;

  const sum = scores.reduce((acc, score) => acc + score, 0);

  let result = 0;
  if (method === 'average') {
    result = sum / scores.length;
  } else {
    result = sum;
  }

  // Ensure the final score is clamped to valid range
  return clampScore(result);
}

/**
 * Clamps a score to the valid range
 * 
 * @param score - The score to clamp
 * @returns The clamped score
 */
export function clampScore(score: number): number {
  return Math.max(MIN_SCORE, Math.min(MAX_SCORE, score));
}

