import { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Timestamp } from 'firebase/firestore';
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Header } from '@/components/Header';
import { BackgroundWrapper } from '@/components/BackgroundWrapper';
import { DetailedResults } from '@/components/DetailedResults';
import { JobValueCompass } from '@/components/results/JobValueCompass';
import { ScoreDisplay, transformScoreForDisplay, getScoreBackgroundColor } from '@/components/ScoreDisplay';
import { ScoreExplainer } from '@/components/ScoreExplainer';
import { CoachingInsights } from '@/components/CoachingInsights';
import { AllCategoryScores } from '@/components/AssessmentForm';
import { useAuth } from '@/context';
import { useAssessmentHistory } from '@/hooks/useAssessmentHistory';
import { useBenchmarks } from '@/hooks/useBenchmarks';
import { INDUSTRY_CATEGORIES, AssessmentData } from '@/models/AssessmentModels';
import { getStorageItem, setStorageItem, STORAGE_KEYS } from '@/utils/storageUtils';
import { getCrossDeviceStorageItem, setCrossDeviceStorageItem } from '@/utils/crossDeviceStorage';
import { getNavigationState, clearNavigationState, isIOSDevice, normalizeImportanceData } from '@/utils/iOSNavigationFix';
import { debugLog } from '@/utils/debugLogger';
import { ChevronLeft, Save, TrendingUp, Building2, Briefcase, BarChart3, Target, Award } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { getCategoryDisplayName, normalizeCategoryId } from '@/utils/categoryDefinitions';

interface JobSatisfactionResults {
  overallScore?: number;
  totalScore?: number;
  categoryScores?: AllCategoryScores;
  categories?: any[];
}

// Helper function to normalize assessment data structure
const normalizeAssessmentData = (data: any): JobSatisfactionResults | null => {
  if (!data) return null;

  // Unwrap data if it's wrapped with metadata (cross-device storage format)
  let actualData = data;
  if (data.value && data.timestamp && data.version) {
    try {
      // Data is wrapped with metadata, extract and parse the actual data
      actualData = typeof data.value === 'string' ? JSON.parse(data.value) : data.value;
      console.log('Unwrapped cross-device storage data:', actualData);
    } catch (e) {
      console.error('Failed to parse wrapped assessment data:', e);
      return null;
    }
  }

  // Handle both localStorage format (overallScore) and Firebase format (totalScore)
  const score = actualData.overallScore !== undefined ? actualData.overallScore : actualData.totalScore;
  let categories = actualData.categoryScores || actualData.categories;

  // Normalize legacy category names (impact ‚Üí purpose, relationships ‚Üí support)
  if (categories && typeof categories === 'object') {
    const normalizedCategories: any = {};
    Object.entries(categories).forEach(([categoryId, categoryData]) => {
      const normalizedId = normalizeCategoryId(categoryId);
      normalizedCategories[normalizedId] = categoryData;
    });
    categories = normalizedCategories;
  }

  if (score === undefined) {
    console.warn('No score found in assessment data:', actualData);
    return null;
  }

  return {
    overallScore: score,
    totalScore: score,
    categoryScores: categories,
    categories: categories
  };
};

