import React, { useState, useEffect } from 'react';
import { MapPin, Building2, Save, Edit2, X, Check } from 'lucide-react';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import axios from 'axios';

interface Venue {
  venue_id: string;
  venue_name: string;
  city: string;
  capacity: number;
}

interface EditingVenue {
  venue_id: string;
  capacity: number;
}

export const VenuesPage: React.FC = () => {
  const [venues, setVenues] = useState<Venue[]>([]);
  const [filteredVenues, setFilteredVenues] = useState<Venue[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [cityFilter, setCityFilter] = useState('');
  const [cities, setCities] = useState<string[]>([]);
  const [editingVenue, setEditingVenue] = useState<EditingVenue | null>(null);
  const [stats, setStats] = useState({
    totalVenues: 0,
    totalCapacity: 0,
    avgCapacity: 0
  });

  useEffect(() => {
    loadVenues();
  }, []);

  useEffect(() => {
    filterVenues();
    calculateStats();
  }, [venues, searchTerm, cityFilter]);

  const loadVenues = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await axios.get('/api/venues');
      const venuesData = response.data.venues;

      setVenues(venuesData);

      // Extract unique cities
      const uniqueCities = [...new Set(venuesData.map((v: Venue) => v.city))].sort();
      setCities(uniqueCities);

    } catch (err) {
      setError('Failed to load venues');
      console.error('Error loading venues:', err);
    } finally {
      setLoading(false);
    }
  };

  const filterVenues = () => {
    let filtered = venues;

    if (searchTerm) {
      filtered = filtered.filter(venue => 
        venue.venue_name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (cityFilter) {
      filtered = filtered.filter(venue => venue.city === cityFilter);
    }

    setFilteredVenues(filtered);
  };

  const calculateStats = () => {
    const venuesList = filteredVenues.length > 0 ? filteredVenues : venues;
    const totalCapacity = venuesList.reduce((sum, venue) => sum + venue.capacity, 0);
    const avgCapacity = venuesList.length > 0 ? Math.round(totalCapacity / venuesList.length) : 0;

    setStats({
      totalVenues: venuesList.length,
      totalCapacity,
      avgCapacity
    });
  };

  const handleEditCapacity = (venue: Venue) => {
    setEditingVenue({
      venue_id: venue.venue_id,
      capacity: venue.capacity
    });
  };

  const handleCancelEdit = () => {
    setEditingVenue(null);
  };

  const handleCapacityChange = (value: string) => {
    if (editingVenue) {
      const capacity = parseInt(value) || 0;
      setEditingVenue({
        ...editingVenue,
        capacity: Math.max(0, capacity) // Ensure non-negative
      });
    }
  };

  const handleSaveCapacity = async () => {
    if (!editingVenue) return;

    try {
      setError(null);
      setSuccess(null);

      // Update venue capacity via API
      const response = await axios.put(`/api/venues/${editingVenue.venue_id}`, {
        capacity: editingVenue.capacity
      });

      // Update local state
      setVenues(venues.map(v => 
        v.venue_id === editingVenue.venue_id 
          ? { ...v, capacity: editingVenue.capacity }
          : v
      ));

      setSuccess(`Successfully updated capacity for venue`);
      setEditingVenue(null);

      // Clear success message after 3 seconds
      setTimeout(() => setSuccess(null), 3000);

    } catch (err) {
      setError('Failed to update venue capacity');
      console.error('Error updating venue:', err);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading venues...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-gray-50">
      {/* Page Header */}
      <div className="bg-white border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Building2 className="w-8 h-8 text-blue-600" />
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Venue Management</h1>
                <p className="text-gray-600">View and edit venue capacities</p>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <div className="text-right">
                <p className="text-sm text-gray-600">Total Venues</p>
                <p className="text-2xl font-bold text-gray-900">{stats.totalVenues}</p>
              </div>
              <div className="text-right">
                <p className="text-sm text-gray-600">Total Capacity</p>
                <p className="text-2xl font-bold text-gray-900">{stats.totalCapacity.toLocaleString()}</p>
              </div>
              <div className="text-right">
                <p className="text-sm text-gray-600">Avg Capacity</p>
                <p className="text-2xl font-bold text-gray-900">{stats.avgCapacity.toLocaleString()}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Filters */}
        <Card className="p-6 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Search Venue
              </label>
              <Input
                type="text"
                placeholder="Search by venue name..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Filter by City
              </label>
              <select
                value={cityFilter}
                onChange={(e) => setCityFilter(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="">All Cities</option>
                {cities.map(city => (
                  <option key={city} value={city}>{city}</option>
                ))}
              </select>
            </div>
            <div className="flex items-end">
              <Button
                variant="outline"
                onClick={() => {
                  setSearchTerm('');
                  setCityFilter('');
                }}
                className="w-full"
              >
                Clear Filters
              </Button>
            </div>
          </div>
        </Card>

        {/* Error/Success Messages */}
        {error && (
          <div className="mb-4 p-4 bg-red-50 border border-red-200 rounded-md">
            <p className="text-red-800">{error}</p>
          </div>
        )}

        {success && (
          <div className="mb-4 p-4 bg-green-50 border border-green-200 rounded-md">
            <p className="text-green-800">{success}</p>
          </div>
        )}

        {/* Venues Table */}
        <Card>
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50 border-b border-gray-200">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Venue Name
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    City
                  </th>
                  <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Capacity
                  </th>
                  <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredVenues.map((venue) => (
                  <tr key={venue.venue_id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <Building2 className="w-5 h-5 text-gray-400 mr-2" />
                        <span className="text-sm font-medium text-gray-900">
                          {venue.venue_name}
                        </span>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <MapPin className="w-4 h-4 text-gray-400 mr-1" />
                        <span className="text-sm text-gray-900">{venue.city}</span>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-right">
                      {editingVenue?.venue_id === venue.venue_id ? (
                        <div className="flex items-center justify-end space-x-2">
                          <Input
                            type="number"
                            value={editingVenue.capacity}
                            onChange={(e) => handleCapacityChange(e.target.value)}
                            className="w-24 text-right"
                            min="0"
                          />
                          <Button
                            size="sm"
                            onClick={handleSaveCapacity}
                            className="p-1"
                          >
                            <Check className="w-4 h-4" />
                          </Button>
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={handleCancelEdit}
                            className="p-1"
                          >
                            <X className="w-4 h-4" />
                          </Button>
                        </div>
                      ) : (
                        <span className="text-sm font-medium text-gray-900">
                          {venue.capacity.toLocaleString()}
                        </span>
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-center">
                      {!editingVenue && (
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => handleEditCapacity(venue)}
                          className="inline-flex items-center"
                        >
                          <Edit2 className="w-4 h-4 mr-1" />
                          Edit
                        </Button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {filteredVenues.length === 0 && (
            <div className="text-center py-8">
              <p className="text-gray-500">No venues found matching your criteria</p>
            </div>
          )}
        </Card>
      </div>
    </div>
  );
};

