            "sales_current_verkaufswoche": "GET /api/sales/current-verkaufswoche",
            "sales_by_verkaufswoche": "GET /api/sales/verkaufswoche/{sales_week}",
            "create_artist": "POST /api/artists",
            "create_venue": "POST /api/venues", 
            "create_tour": "POST /api/tours",
            "create_concert": "POST /api/concerts",
            "create_weekly_sales": "POST /api/sales/weekly",
            "update_weekly_sales": "PUT /api/sales/{sale_id}",
            "bulk_weekly_sales": "POST /api/sales/bulk",
