@app.post("/api/sales/weekly")
def create_weekly_sales(sales: WeeklySalesCreate):
    """Create weekly sales entry"""
    try:
        # Validate concert exists
        concerts = csv_manager.read_csv('concerts')
        concert = None
        for c in concerts:
            if c['concert_id'] == sales.concert_id:
                concert = c
                break

        if not concert:
            raise HTTPException(status_code=400, detail="Concert not found")

        # Validate venue capacity
        if not csv_manager.validate_venue_capacity(concert['venue_id'], sales.tickets_sold_total):
            raise HTTPException(status_code=400, detail="Tickets sold exceeds venue capacity")

        # Validate tickets_sold_total >= tickets_sold_in_week
        if sales.tickets_sold_total < sales.tickets_sold_in_week:
            raise HTTPException(status_code=400, detail="Total tickets sold cannot be less than weekly sales")

        sale_id = csv_manager.add_record('weekly_sales', {
            'concert_id': sales.concert_id,
            'calendar_week': sales.calendar_week,
            'sales_week_number': str(sales.sales_week_number),
            'tickets_sold_in_week': str(sales.tickets_sold_in_week),
            'tickets_sold_total': str(sales.tickets_sold_total)
        })

        return {
            "message": "Weekly sales entry created successfully",
            "sale_id": sale_id,
            "concert_id": sales.concert_id,
            "calendar_week": sales.calendar_week,
            "tickets_sold_in_week": sales.tickets_sold_in_week,
