          element={
            status?.ready 
              ? <Navigate to="/dashboard" replace />
              : <Navigate to="/upload" replace />
          } 
        />
        <Route 
          path="/upload" 
          element={
            <Layout>
              <UploadPage status={status} />
            </Layout>
          } 
        />
        <Route 
          path="/dashboard" 
          element={
            <Layout>
              <DashboardPage />
            </Layout>
          } 
        />
        <Route 
          path="/data-entry" 
          element={
            <Layout>
              <DataEntryPage />
            </Layout>
          } 
        />
      </Routes>
    </Router>
  );
}

export default App;

