from fastapi import FastAPI, HTTPException, Query
from fastapi.middleware.cors import CORSMiddleware
from data_processor_v2 import DataProcessorV2
from csv_manager import CSVManager
from pydantic import BaseModel, Field
from typing import Optional, List, Dict, Any
import io
import os
from pathlib import Path
from datetime import datetime

app = FastAPI()

# Configure CORS
app.add_middleware(
    CORSMiddleware,
    allow_origins=["http://localhost:3000", "http://localhost:3001", "http://localhost:3002", "http://localhost:3003"],
    allow_credentials=True,
    allow_methods=["*"],
    allow_headers=["*"],
)

# Global data processor and CSV manager
data_processor = None
csv_manager = CSVManager()
original_csv_files = None
dataset_status = {
    "data_loaded": False,
    "data_source": "csv_files",
    "csv_files_found": [],
    "missing_files": [],
    "load_error": None,
    "artists_count": 0
}

# Pydantic models for request validation
class ArtistCreate(BaseModel):
    artist_name: str = Field(..., min_length=1, max_length=100)

class VenueCreate(BaseModel):
    venue_name: str = Field(..., min_length=1, max_length=100)
    city: str = Field(..., min_length=1, max_length=100)
    capacity: int = Field(..., ge=1, le=100000)

class VenueUpdate(BaseModel):
    capacity: int = Field(..., ge=1, le=100000)

class TourCreate(BaseModel):
    tour_name: str = Field(..., min_length=1, max_length=100)
    artist_id: str = Field(..., min_length=1)

class ConcertCreate(BaseModel):
    concert_date: str = Field(..., pattern=r"\d{2}\.\d{2}\.\d{4}")  # DD.MM.YYYY format
    tour_id: str = Field(..., min_length=1)
    venue_id: str = Field(..., min_length=1)

class WeeklySalesCreate(BaseModel):
    concert_id: str = Field(..., min_length=1)
    calendar_week: str = Field(..., pattern=r"KW \d{1,2}")  # KW XX format
    sales_week_number: int = Field(..., ge=1, le=52)
    tickets_sold_in_week: int = Field(..., ge=0)
    tickets_sold_total: int = Field(..., ge=0)

class WeeklySalesUpdate(BaseModel):
    tickets_sold_in_week: int = Field(..., ge=0)
    tickets_sold_total: int = Field(..., ge=0)

class BulkWeeklySalesEntry(BaseModel):
    entries: List[Dict[str, Any]] = Field(..., min_items=1)

def check_csv_dataset():
    """Check if complete CSV dataset exists"""
    global dataset_status

    # Define the folder path (relative to backend directory)
    folder_path = Path("../../VVK_Zahlen")
    required_files = ['artists', 'venues', 'tours', 'concerts', 'weekly_sales']

    dataset_status["csv_files_found"] = []
    dataset_status["missing_files"] = []

    for filename in required_files:
        file_path = folder_path / f"{filename}.csv"
        if file_path.exists():
            dataset_status["csv_files_found"].append(f"{filename}.csv")
        else:
            dataset_status["missing_files"].append(f"{filename}.csv")

    # Dataset is complete if all required files are found
    return len(dataset_status["missing_files"]) == 0

def load_csv_data():
    """Load CSV data on startup"""
    global data_processor, original_csv_files, dataset_status

    try:
        print("ğŸ” Checking for CSV dataset...")

        if not check_csv_dataset():
            missing_files_str = ", ".join(dataset_status["missing_files"])
            error_msg = f"Incomplete dataset. Missing files: {missing_files_str}"
            print(f"âŒ {error_msg}")
            dataset_status["load_error"] = error_msg
            return False

        print("âœ… Complete CSV dataset found!")
        print("ğŸš€ Loading CSV data...")

        # Define the folder path (relative to backend directory)
        folder_path = "../../VVK_Zahlen"

        # Load CSV files from folder
        csv_files = {}
        required_files = ['artists', 'venues', 'tours', 'concerts', 'weekly_sales']

        for filename in required_files:
            file_path = os.path.join(folder_path, f"{filename}.csv")

            with open(file_path, 'rb') as f:
                csv_files[filename] = io.BytesIO(f.read())
            print(f"âœ… Loaded {filename}.csv")

        # Store original files for artist filtering
        original_csv_files = csv_files

        # Create data processor without artist filter
        data_processor = DataProcessorV2(csv_files)

        artists_count = len(data_processor.get_available_artists())
        dataset_status["data_loaded"] = True
        dataset_status["artists_count"] = artists_count
        dataset_status["load_error"] = None

        print(f"ğŸ‰ Successfully loaded {artists_count} artists from CSV data!")
        print("ğŸ“Š Dashboard ready to serve data!")

        return True

    except Exception as e:
        error_msg = f"Error loading CSV data: {str(e)}"
        print(f"âŒ {error_msg}")
        dataset_status["load_error"] = error_msg
        dataset_status["data_loaded"] = False
        import traceback
        traceback.print_exc()
        return False

# Load data on startup
print("ğŸ Starting Sales Dashboard API v2.0...")
if not load_csv_data():
    print("âš ï¸  Failed to load CSV data on startup. Dashboard will run in limited mode.")
    print("ğŸ’¡ Place complete CSV dataset in VVK_Zahlen/ directory and restart the server.")

@app.get("/")
def read_root():
    return {
        "message": "Sales Dashboard API v2.0 - CSV Database Mode",
        "version": "2.0.0",
        "data_source": dataset_status["data_source"],
        "dataset_status": dataset_status,
        "ready": dataset_status["data_loaded"],
        "endpoints": {
            "status": "GET /api/status",
            "artists": "GET /api/artists",
            "venues": "GET /api/venues",
            "tours": "GET /api/tours",
            "concerts": "GET /api/concerts",
            "sales_current_week": "GET /api/sales/current-week",
            "sales_by_week": "GET /api/sales/week/{week_number}",
            "sales_current_verkaufswoche": "GET /api/sales/current-verkaufswoche",
            "sales_by_verkaufswoche": "GET /api/sales/verkaufswoche/{sales_week}",
            "create_artist": "POST /api/artists",
            "create_venue": "POST /api/venues", 
            "create_tour": "POST /api/tours",
            "create_concert": "POST /api/concerts",
            "create_weekly_sales": "POST /api/sales/weekly",
            "update_weekly_sales": "PUT /api/sales/{sale_id}",
            "bulk_weekly_sales": "POST /api/sales/bulk",
            "kpis": "GET /api/kpis?artist=<artist_name>",
            "sales_over_time": "GET /api/sales-over-time?artist=<artist_name>",
            "sales_trends": "GET /api/sales-trends?artist=<artist_name>",
            "sales_by_city_over_time": "GET /api/sales-by-city-over-time?artist=<artist_name>",
            "reload_data": "POST /reload-data",
            "docs": "GET /docs"
        }
    }

@app.get("/api/status")
def get_status():
    """Get detailed status about data availability"""
    return {
        "ready": dataset_status["data_loaded"],
        "data_source": dataset_status["data_source"],
        "dataset_info": {
            "csv_files_found": dataset_status["csv_files_found"],
            "missing_files": dataset_status["missing_files"],
            "artists_count": dataset_status["artists_count"]
        },
        "error": dataset_status["load_error"]
    }

@app.post("/reload-data/")
async def reload_data():
    """Reload CSV data"""
    if load_csv_data():
        return {
            "message": "CSV data reloaded successfully",
            "artists_loaded": len(data_processor.get_available_artists()) if data_processor else 0
        }
    else:
        raise HTTPException(status_code=500, detail="Failed to reload CSV data")

# Data Input API Endpoints

@app.get("/api/venues")
def get_venues():
    """Get all venues"""
    try:
        venues = csv_manager.read_csv('venues')
        return {"venues": venues}
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading venues: {str(e)}")

@app.get("/api/tours")
def get_tours():
    """Get all tours"""
    try:
        tours = csv_manager.read_csv('tours')
        return {"tours": tours}
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading tours: {str(e)}")

@app.get("/api/concerts")
def get_concerts(artist_id: Optional[str] = Query(None), tour_id: Optional[str] = Query(None)):
    """Get concerts with optional filtering"""
    try:
        concerts = csv_manager.get_concerts_for_current_week()

        # Apply filters
        if artist_id:
            concerts = [c for c in concerts if c.get('artist_id') == artist_id]
        if tour_id:
            concerts = [c for c in concerts if c.get('tour_id') == tour_id]

        return {"concerts": concerts}
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading concerts: {str(e)}")

@app.get("/api/sales/current-week")
def get_current_week_sales():
    """Get current week sales data"""
    try:
        current_week = csv_manager.get_current_week()
        weekly_sales = csv_manager.read_csv('weekly_sales')

        # Filter for current week
        current_week_sales = [
            sale for sale in weekly_sales 
            if sale.get('calendar_week') == f"KW {current_week}"
        ]

        return {
            "current_week": current_week,
            "calendar_week": f"KW {current_week}",
            "sales": current_week_sales
        }
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading current week sales: {str(e)}")

@app.get("/api/sales/week/{week_number}")
def get_sales_by_week(week_number: int):
    """Get sales data for a specific week"""
    try:
        if week_number < 1 or week_number > 53:
            raise HTTPException(status_code=400, detail="Week number must be between 1 and 53")

        weekly_sales = csv_manager.read_csv('weekly_sales')

        # Filter for specified week
        week_sales = [
            sale for sale in weekly_sales 
            if sale.get('calendar_week') == f"KW {week_number}"
        ]

        return {
            "week": week_number,
            "calendar_week": f"KW {week_number}",
            "sales": week_sales
        }
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading week {week_number} sales: {str(e)}")

