@app.put("/api/venues/{venue_id}")
def update_venue_capacity(venue_id: str, venue: VenueUpdate):
    """Update venue capacity"""
    try:
        # Check if venue exists
        venues = csv_manager.read_csv('venues')
        venue_exists = any(v['venue_id'] == venue_id for v in venues)

        if not venue_exists:
            raise HTTPException(status_code=404, detail="Venue not found")

        # Update venue capacity
        success = csv_manager.update_record('venues', venue_id, {
            'capacity': str(venue.capacity)
