                  <th className="border border-gray-200 px-4 py-2 text-left">Concert</th>
                  <th className="border border-gray-200 px-4 py-2 text-left">Venue</th>
                  <th className="border border-gray-200 px-4 py-2 text-left">City</th>
                  <th className="border border-gray-200 px-4 py-2 text-left">Artist</th>
                  <th className="border border-gray-200 px-4 py-2 text-right">Capacity</th>
                  <th className="border border-gray-200 px-4 py-2 text-right">Prev Week</th>
                  <th className="border border-gray-200 px-4 py-2 text-right">This Week</th>
                  <th className="border border-gray-200 px-4 py-2 text-right">Total</th>
                </tr>
              </thead>
              <tbody>
                {salesEntries.map((entry) => (
                  <tr key={entry.concert_id} className="hover:bg-gray-50">
                    <td className="border border-gray-200 px-4 py-2">
                      {entry.concert_date}
                    </td>
                    <td className="border border-gray-200 px-4 py-2">
                      {entry.venue_name}
                    </td>
                    <td className="border border-gray-200 px-4 py-2">
                      {entry.city}
                    </td>
                    <td className="border border-gray-200 px-4 py-2">
                      {entry.artist_name}
                    </td>
                    <td className="border border-gray-200 px-4 py-2 text-right">
                      {entry.capacity.toLocaleString()}
                    </td>
                    <td className="border border-gray-200 px-4 py-2 text-right">
                      {entry.previous_week_sales.toLocaleString()}
                    </td>
                    <td className="border border-gray-200 px-4 py-2">
                      <input
                        type="number"
                        min="0"
                        max={entry.capacity}
                        value={entry.tickets_sold_in_week}
                        onChange={(e) => handleSalesChange(entry.concert_id, 'tickets_sold_in_week', parseInt(e.target.value) || 0)}
                        className="w-full px-2 py-1 border border-gray-300 rounded text-right"
