                    </td>
                    <td className="border border-gray-200 px-4 py-2">
                      <input
                        type="number"
                        min="0"
                        max={entry.capacity}
                        value={entry.tickets_sold_total}
                        onChange={(e) => handleSalesChange(entry.concert_id, 'tickets_sold_total', parseInt(e.target.value) || 0)}
                        className="w-full px-2 py-1 border border-gray-300 rounded text-right"
