@app.get("/api/sales/week/{week_number}")
def get_sales_by_week(week_number: int):
    """Get sales data for a specific week"""
    try:
        if week_number < 1 or week_number > 53:
            raise HTTPException(status_code=400, detail="Week number must be between 1 and 53")

        weekly_sales = csv_manager.read_csv('weekly_sales')

        # Filter for specified week
        week_sales = [
            sale for sale in weekly_sales 
            if sale.get('calendar_week') == f"KW {week_number}"
        ]

        return {
            "week": week_number,
            "calendar_week": f"KW {week_number}",
            "sales": week_sales
        }
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading week {week_number} sales: {str(e)}")

@app.get("/api/sales/verkaufswoche/{sales_week}")
def get_sales_by_verkaufswoche(sales_week: int):
    """Get sales data for a specific Verkaufswoche (sales week number)"""
    try:
        if sales_week < 1:
            raise HTTPException(status_code=400, detail="Sales week number must be positive")

        weekly_sales = csv_manager.read_csv('weekly_sales')

        # Filter for specified Verkaufswoche
        week_sales = [
            sale for sale in weekly_sales 
            if int(sale.get('sales_week_number', 0)) == sales_week
        ]

        return {
            "sales_week": sales_week,
            "sales": week_sales
        }
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading Verkaufswoche {sales_week} sales: {str(e)}")

