@app.get("/api/sales/current-verkaufswoche")
def get_current_verkaufswoche_sales():
    """Get current Verkaufswoche sales data with previous week info"""
    try:
        weekly_sales = csv_manager.read_csv('weekly_sales')

        # Default to week 48 as current week (can be made configurable)
        current_sales_week = 48

        if not weekly_sales:
            return {
                "current_sales_week": current_sales_week,
                "previous_sales_week": None,
                "available_sales_weeks": [],
                "sales": [],
                "previous_week_available": False
            }

        # Get all available sales weeks from the data
        available_weeks = sorted(set(int(sale.get('sales_week_number', 1)) for sale in weekly_sales))

        # For data entry, we want to show the last week with actual data
        # This is the most recent week in the data
        previous_sales_week = max(available_weeks) if available_weeks else None

        # Get current week sales (likely empty for week 48)
        current_week_sales = [
            sale for sale in weekly_sales 
            if int(sale.get('sales_week_number', 0)) == current_sales_week
        ]

        return {
            "current_sales_week": current_sales_week,
            "previous_sales_week": previous_sales_week,
            "available_sales_weeks": available_weeks,
            "sales": current_week_sales,
            "previous_week_available": previous_sales_week is not None
        }
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error reading current Verkaufswoche sales: {str(e)}")

@app.get("/api/sales/artist-week-ranges")
def get_artist_week_ranges():
    """Get Verkaufswoche ranges for each artist"""
    try:
        artists = csv_manager.read_csv('artists')
        weekly_sales = csv_manager.read_csv('weekly_sales')
        concerts = csv_manager.read_csv('concerts')
        tours = csv_manager.read_csv('tours')

        if not artists:
            return {"max_week": 0, "artist_ranges": {}}

        # Create lookup dictionaries
        tour_to_artist = {}
        for tour in tours:
            tour_to_artist[tour['tour_id']] = tour.get('artist_id')

        concert_to_tour = {}
        for concert in concerts:
            concert_to_tour[concert['concert_id']] = concert.get('tour_id')

        # Track sales weeks per artist
        artist_weeks = {}
        for artist in artists:
            artist_weeks[artist['artist_name']] = set()

        # Process weekly sales to find week ranges
        for sale in weekly_sales:
            concert_id = sale.get('concert_id')
            if concert_id in concert_to_tour:
                tour_id = concert_to_tour[concert_id]
                if tour_id in tour_to_artist:
                    artist_id = tour_to_artist[tour_id]
                    # Find artist name
                    for artist in artists:
                        if artist['artist_id'] == artist_id:
                            week_num = int(sale.get('sales_week_number', 0))
                            if week_num > 0:
                                artist_weeks[artist['artist_name']].add(week_num)
                            break

        # Calculate ranges
        artist_ranges = {}
        max_week = 0

        for artist_name, weeks in artist_weeks.items():
            if weeks:
                min_week = min(weeks)
                max_week_artist = max(weeks)
                artist_ranges[artist_name] = {
                    "min": min_week,
                    "max": max_week_artist,
                    "total": max_week_artist - min_week + 1,
                    "weeks": sorted(list(weeks))
                }
                max_week = max(max_week, max_week_artist)
            else:
                artist_ranges[artist_name] = {
                    "min": 0,
                    "max": 0,
                    "total": 0,
                    "weeks": []
                }

        return {
            "max_week": max_week,
            "current_week": 48,  # This should be configurable
            "artist_ranges": artist_ranges
        }
    except Exception as e:
        raise HTTPException(status_code=500, detail=f"Error getting artist week ranges: {str(e)}")

