import React, { useState, useEffect } from 'react';
import { Database, Search, X, ChevronLeft, ChevronRight } from 'lucide-react';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { dataEntryApi, Concert, Artist, ArtistWeekRangesResponse, ArtistWeekRange } from '@/services/dataEntryApi';

interface WeeklySalesEntry {
  concert_id: string;
  concert_date: string;
  venue_name: string;
  city: string;
  artist_name: string;
  tour_name: string;
  capacity: number;
  tickets_sold_in_week: number;
  tickets_sold_total: number;
  previous_week_sales: number;
  is_sold_out: boolean;
  status?: string;
}

export const DataEntryPage: React.FC = () => {
  const [concerts, setConcerts] = useState<Concert[]>([]);
  const [artists, setArtists] = useState<Artist[]>([]);
  const [selectedArtist, setSelectedArtist] = useState<string>('');
  const [currentWeek, setCurrentWeek] = useState<number>(48);
  const [selectedVerkaufswoche, setSelectedVerkaufswoche] = useState<number>(48);
  const [calendarWeek, setCalendarWeek] = useState<string>('KW 48');
  const [salesEntries, setSalesEntries] = useState<WeeklySalesEntry[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);
  const [artistSearch, setArtistSearch] = useState<string>('');
  const [artistWeekRanges, setArtistWeekRanges] = useState<Record<string, ArtistWeekRange>>({});
  const [maxVerkaufswoche, setMaxVerkaufswoche] = useState<number>(48);

  useEffect(() => {
    loadInitialData();
  }, []);

  useEffect(() => {
    // Load week data whenever the selected week changes or concerts are loaded
    if (concerts.length > 0) {
      loadWeekData(selectedVerkaufswoche);
    }
  }, [selectedVerkaufswoche, concerts]);

  const loadInitialData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Load artists
      const artistsResponse = await dataEntryApi.getArtists();
      setArtists(artistsResponse.artists || []);

      // Load current Verkaufswoche sales data (not calendar week!)
      const currentVerkaufsData = await dataEntryApi.getCurrentVerkaufswoche();
      setCurrentWeek(currentVerkaufsData.current_sales_week);
      setSelectedVerkaufswoche(currentVerkaufsData.current_sales_week);
      // Note: We use KW for display but this represents the sales week number (Verkaufswoche)
      setCalendarWeek(`KW ${currentVerkaufsData.current_sales_week}`);

      console.log(`âœ… Current Verkaufswoche: ${currentVerkaufsData.current_sales_week}`);
      console.log(`ğŸ“Š Previous Verkaufswoche: ${currentVerkaufsData.previous_sales_week}`);
      console.log(`ğŸ“ˆ Available weeks: ${currentVerkaufsData.available_sales_weeks}`);

      // Load concerts
      const { concerts: concertsResponse } = await dataEntryApi.getConcerts();
      setConcerts(concertsResponse);

      // Don't load sales entries here - let the useEffect handle it based on selectedVerkaufswoche
    } catch (err) {
      setError('Failed to load initial data');
      console.error('Error loading data:', err);
    } finally {
      setLoading(false);
    }
  };

  const loadWeekData = async (weekNumber: number) => {
    // Don't load if concerts haven't been loaded yet
    if (!concerts || concerts.length === 0) {
      return;
    }

    try {
      setLoading(true);
      setError(null);
      setSuccess(null);

      // Update display week
      setCalendarWeek(`KW ${weekNumber}`);

      // Load existing sales data for the selected week
      const weekSalesData = await dataEntryApi.getSalesByVerkaufswoche(weekNumber);

      // Get previous week data
      const { previousWeekSalesMap, soldOutStatusMap } = await calculatePreviousWeekSales(weekNumber);

      // Create a map of existing sales data
      const existingSalesMap = new Map<string, any>();
      weekSalesData.sales.forEach(sale => {
        existingSalesMap.set(sale.concert_id, sale);
      });

      // Update sales entries with existing data
      const updatedEntries = concerts.map(concert => {
        const existingSale = existingSalesMap.get(concert.concert_id);
        return {
          concert_id: concert.concert_id,
          concert_date: concert.concert_date,
          venue_name: concert.venue_name || '',
          city: concert.city || '',
          artist_name: concert.artist_name || '',
          tour_name: concert.tour_name || '',
          capacity: concert.capacity || 0,
          tickets_sold_in_week: existingSale ? parseInt(existingSale.tickets_sold_in_week) || 0 : 0,
          tickets_sold_total: existingSale ? parseInt(existingSale.tickets_sold_total) || 0 : 0,
          previous_week_sales: previousWeekSalesMap.get(concert.concert_id) || 0,
          is_sold_out: soldOutStatusMap.get(concert.concert_id) || false,
          status: soldOutStatusMap.get(concert.concert_id) ? 'sold_out' : (concert.status || 'active')
        };
      });

      // Apply artist filter if one is selected
      if (selectedArtist) {
        const filteredEntries = updatedEntries.filter(entry => entry.artist_name === selectedArtist);
        setSalesEntries(filteredEntries);
      } else {
        setSalesEntries(updatedEntries);
      }

    } catch (err) {
      setError('Failed to load week data');
      console.error('Error loading week data:', err);
    } finally {
      setLoading(false);
    }
  };

  const calculatePreviousWeekSales = async (currentWeekNumber?: number) => {
    try {
      const previousWeekSalesMap = new Map<string, number>();
      const soldOutStatusMap = new Map<string, boolean>();

      // Get concert status for sold out information
      const concertsLastWeekData = await dataEntryApi.getConcertsLastWeekData();

      // Map sold out status
      if (concertsLastWeekData.concerts_last_week) {
        Object.entries(concertsLastWeekData.concerts_last_week).forEach(([concertId, data]) => {
          if (data.is_sold_out) {
            soldOutStatusMap.set(concertId, true);
          }
        });
      }

      // If we have a specific week number, get data for the previous week
      const targetWeek = currentWeekNumber || selectedVerkaufswoche;

      if (targetWeek > 1) {
        // Get previous week data
        const previousWeek = targetWeek - 1;
        console.log(`ğŸ” Loading Verkaufswoche ${previousWeek} data (previous to week ${targetWeek})...`);

        try {
          const previousWeekData = await dataEntryApi.getSalesByVerkaufswoche(previousWeek);

          // Map previous week sales
          previousWeekData.sales.forEach(sale => {
            const weekSales = typeof sale.tickets_sold_in_week === 'string' 
              ? parseInt(sale.tickets_sold_in_week) 
              : sale.tickets_sold_in_week;

            // For sold out concerts, always show 0
            if (soldOutStatusMap.get(sale.concert_id)) {
              previousWeekSalesMap.set(sale.concert_id, 0);
            } else if (weekSales >= 0) {
              previousWeekSalesMap.set(sale.concert_id, weekSales);
            }
          });

          console.log(`âœ… Loaded week ${previousWeek} data for ${previousWeekData.sales.length} concerts`);
        } catch (error) {
          console.log(`â„¹ï¸ No data available for Verkaufswoche ${previousWeek}`);
        }
      } else {
        console.log(`â„¹ï¸ Week 1 has no previous week data`);
      }

      console.log(`ğŸš« ${soldOutStatusMap.size} concerts are sold out`);
      return { previousWeekSalesMap, soldOutStatusMap };
    } catch (error) {
      console.error('Error calculating previous week sales:', error);
      return { previousWeekSalesMap: new Map<string, number>(), soldOutStatusMap: new Map<string, boolean>() };
    }
  };

  const handleArtistFilter = async (artistName: string) => {
    setSelectedArtist(artistName);
    // Clear search when an artist is selected
    setArtistSearch('');

    // Reload the week data with the new artist filter
    await loadWeekData(selectedVerkaufswoche);
  };

  const handleSalesChange = (concertId: string, field: 'tickets_sold_in_week' | 'tickets_sold_total', value: number) => {
    setSalesEntries(prev => 
      prev.map(entry => 
        entry.concert_id === concertId
          ? { ...entry, [field]: value }
          : entry
      )
    );
  };

  const handleSaveAll = async () => {
    try {
      setLoading(true);
      setError(null);
      setSuccess(null);

      const bulkEntries = salesEntries
        .filter(entry => entry.tickets_sold_in_week > 0 || entry.tickets_sold_total > 0)
        .map(entry => ({
          concert_id: entry.concert_id,
          calendar_week: `KW ${selectedVerkaufswoche}`,
          sales_week_number: selectedVerkaufswoche,
          tickets_sold_in_week: entry.tickets_sold_in_week,
          tickets_sold_total: entry.tickets_sold_total
        }));

      if (bulkEntries.length === 0) {
        setError('No sales data to save');
        return;
      }

      const result = await dataEntryApi.bulkCreateWeeklySales({ entries: bulkEntries });

      if (result.successful_entries > 0) {
        setSuccess(`Successfully saved ${result.successful_entries} sales entries for Verkaufswoche ${selectedVerkaufswoche}`);

        if (result.errors.length > 0) {
          setError(`Some entries failed: ${result.errors.join(', ')}`);
        }
      } else {
        setError('Failed to save sales entries');
      }
    } catch (err) {
      setError('Failed to save sales data');
      console.error('Error saving sales:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setSalesEntries(prev => 
      prev.map(entry => ({
        ...entry,
        tickets_sold_in_week: 0,
        tickets_sold_total: 0
      }))
    );
    setError(null);
    setSuccess(null);
  };

  if (loading && salesEntries.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading sales data...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-gray-50">
      {/* Page Header */}
      <div className="bg-white border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between mb-6">
            <div>
              <div className="flex items-center space-x-2">
                <Database className="w-8 h-8 text-blue-600" />
                <div>
                  <h1 className="text-2xl font-bold text-gray-900">Sales Entry</h1>
                  <p className="text-gray-600">Enter weekly sales data for concerts</p>
                </div>
              </div>
            </div>
            <div className="flex gap-4">
              <Button 
                variant="outline" 
                onClick={handleReset}
                disabled={loading}
              >
                Reset
              </Button>
              <Button 
                onClick={handleSaveAll}
                disabled={loading || salesEntries.length === 0}
              >
                {loading ? 'Saving...' : 'Save All'}
              </Button>
            </div>
          </div>

          {/* Verkaufswoche Selector */}
          <div className="flex items-center justify-center gap-4 mt-4">
            <Button
              variant="outline"
              size="sm"
              onClick={() => setSelectedVerkaufswoche(prev => Math.max(1, prev - 1))}
              disabled={loading || selectedVerkaufswoche <= 1}
            >
              <ChevronLeft className="w-4 h-4" />
            </Button>

            <div className="text-center">
              <div className="flex items-center gap-2">
                <h2 className="text-lg font-semibold text-gray-900">
                  Verkaufswoche
                </h2>
                <select
                  value={selectedVerkaufswoche}
                  onChange={(e) => setSelectedVerkaufswoche(parseInt(e.target.value))}
                  disabled={loading}
                  className="px-3 py-1 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  {Array.from({ length: currentWeek }, (_, i) => i + 1).map(week => (
                    <option key={week} value={week}>
                      {week}
                    </option>
                  ))}
                </select>
              </div>
              <p className="text-sm text-gray-500">
                {selectedVerkaufswoche === currentWeek ? '(Current Week)' : 
                 selectedVerkaufswoche < currentWeek ? '(Past Week)' : '(Future Week)'}
              </p>
            </div>

            <Button
              variant="outline"
              size="sm"
              onClick={() => setSelectedVerkaufswoche(prev => prev + 1)}
              disabled={loading || selectedVerkaufswoche >= currentWeek}
            >
              <ChevronRight className="w-4 h-4" />
            </Button>

            {selectedVerkaufswoche !== currentWeek && (
              <Button
                variant="outline"
                size="sm"
                onClick={() => setSelectedVerkaufswoche(currentWeek)}
                disabled={loading}
                className="ml-4"
              >
                Current Week
              </Button>
            )}
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <Card className="p-6">
          {/* Artist Filter */}
          <div className="mb-6">
            {/* Artist Search */}
            <div className="mb-4">
              <div className="relative w-full max-w-md">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Search className="h-4 w-4 text-gray-400" />
                </div>
                <input
                  type="text"
                  placeholder="Search artists..."
                  value={artistSearch}
                  onChange={(e) => setArtistSearch(e.target.value)}
                  className="w-full pl-10 pr-10 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
                {artistSearch && (
                  <button
                    onClick={() => setArtistSearch('')}
                    className="absolute inset-y-0 right-0 pr-3 flex items-center"
                  >
                    <X className="h-4 w-4 text-gray-400 hover:text-gray-600" />
                  </button>
                )}
              </div>
            </div>

            {/* Artist Filter Buttons */}
            <div className="flex gap-2 flex-wrap max-h-32 overflow-y-auto">
              <Button
                variant={selectedArtist === '' ? 'default' : 'outline'}
                onClick={() => handleArtistFilter('')}
                className="text-sm flex-shrink-0"
              >
                All Artists ({artists.length})
              </Button>
              {artists
                .filter(artist => 
                  artistSearch === '' || 
                  artist.artist_name.toLowerCase().includes(artistSearch.toLowerCase())
                )
                .map((artist) => (
                <Button
                  key={artist.artist_id}
                  variant={selectedArtist === artist.artist_name ? 'default' : 'outline'}
                  onClick={() => handleArtistFilter(artist.artist_name)}
                  className="text-sm flex-shrink-0"
                >
                  {artist.artist_name}
                </Button>
              ))}
            </div>
            <p className="text-xs text-gray-500 mt-2">
              {artistSearch 
                ? `Showing ${artists.filter(artist => artist.artist_name.toLowerCase().includes(artistSearch.toLowerCase())).length} of ${artists.length} artists`
                : `Showing all ${artists.length} artists`
              }. Use the search box or filter buttons to focus on specific artists.
            </p>
          </div>

          {/* Error/Success Messages */}
          {error && (
            <div className="mb-4 p-4 bg-red-50 border border-red-200 rounded-md">
              <p className="text-red-800">{error}</p>
            </div>
          )}

          {success && (
            <div className="mb-4 p-4 bg-green-50 border border-green-200 rounded-md">
              <p className="text-green-800">{success}</p>
            </div>
          )}

          {/* Sales Entry Table */}
          <div className="overflow-x-auto">
            <table className="w-full border-collapse border border-gray-200">
              <thead>
                <tr className="bg-gray-50">
                  <th className="border border-gray-200 px-4 py-2 text-left">Concert</th>
                  <th className="border border-gray-200 px-4 py-2 text-left">Venue</th>
                  <th className="border border-gray-200 px-4 py-2 text-left">City</th>
                  <th className="border border-gray-200 px-4 py-2 text-left">Artist</th>
                  <th className="border border-gray-200 px-4 py-2 text-right">Capacity</th>
                  <th className="border border-gray-200 px-4 py-2 text-right">Prev Week</th>
                  <th className="border border-gray-200 px-4 py-2 text-right">This Week</th>
                  <th className="border border-gray-200 px-4 py-2 text-right">Total</th>
                </tr>
              </thead>
              <tbody>
                {salesEntries.map((entry) => (
                  <tr key={entry.concert_id} className={`hover:bg-gray-50 ${entry.is_sold_out ? 'bg-red-50' : ''}`}>
                    <td className="border border-gray-200 px-4 py-2">
                      {entry.concert_date}
                    </td>
                    <td className="border border-gray-200 px-4 py-2">
                      <div className="flex items-center justify-between">
                        <span>{entry.venue_name}</span>
                        {entry.is_sold_out && (
                          <span className="ml-2 px-2 py-1 text-xs font-semibold text-red-700 bg-red-100 rounded">
                            AUSVERKAUFT
                          </span>
                        )}
                      </div>
                    </td>
                    <td className="border border-gray-200 px-4 py-2">
                      {entry.city}
                    </td>
                    <td className="border border-gray-200 px-4 py-2">
                      {entry.artist_name}
                    </td>
                    <td className="border border-gray-200 px-4 py-2 text-right">
                      {entry.capacity.toLocaleString()}
                    </td>
                    <td className="border border-gray-200 px-4 py-2 text-right">
                      {entry.previous_week_sales.toLocaleString()}
                    </td>
                    <td className="border border-gray-200 px-4 py-2">
                      <input
                        type="number"
                        min="0"
                        max={entry.capacity}
                        value={entry.tickets_sold_in_week}
                        onChange={(e) => handleSalesChange(entry.concert_id, 'tickets_sold_in_week', parseInt(e.target.value) || 0)}
                        className={`w-full px-2 py-1 border border-gray-300 rounded text-right ${
                          entry.is_sold_out ? 'bg-gray-100 cursor-not-allowed opacity-50' : ''
                        }`}
                        disabled={entry.is_sold_out}
                      />
                    </td>
                    <td className="border border-gray-200 px-4 py-2">
                      <input
                        type="number"
                        min="0"
                        max={entry.capacity}
                        value={entry.tickets_sold_total}
                        onChange={(e) => handleSalesChange(entry.concert_id, 'tickets_sold_total', parseInt(e.target.value) || 0)}
                        className={`w-full px-2 py-1 border border-gray-300 rounded text-right ${
                          entry.is_sold_out ? 'bg-gray-100 cursor-not-allowed opacity-50' : ''
                        }`}
                        disabled={entry.is_sold_out}
                      />
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {salesEntries.length === 0 && (
            <div className="text-center py-8">
              <p className="text-gray-500">No concerts available for data entry</p>
            </div>
          )}
        </Card>
      </div>
    </div>
  );
};

