from fastapi import FastAPI, File, UploadFile, HTTPException
from fastapi.middleware.cors import CORSMiddleware
from data_processor import DataProcessor

app = FastAPI()

origins = [
    "http://localhost:3000",  # React app
    "http://localhost:3001",
]

app.add_middleware(
    CORSMiddleware,
    allow_origins=origins,
    allow_credentials=True,
    allow_methods=["*"],
    allow_headers=["*"],
)

# This will store the data processor instance in-memory
data_processor: DataProcessor = None

@app.post("/upload/")
async def upload_file(file: UploadFile = File(...)):
    global data_processor
    try:
        data_processor = DataProcessor(file.file)
        return {"message": "File uploaded and processed successfully."}
    except ValueError as e:
        raise HTTPException(status_code=400, detail=str(e))

@app.get("/api/kpis")
def get_kpis():
    if not data_processor:
        raise HTTPException(status_code=404, detail="No data available. Please upload a file first.")
    return data_processor.calculate_kpis()

@app.get("/api/sales-by-artist")
def get_sales_by_artist():
    if not data_processor:
        raise HTTPException(status_code=404, detail="No data available. Please upload a file first.")
    return data_processor.calculate_sales_by_artist()

@app.get("/api/sales-over-time")
def get_sales_over_time():
    if not data_processor:
        raise HTTPException(status_code=404, detail="No data available. Please upload a file first.")
    return data_processor.calculate_sales_over_time()



@app.get("/api/sales-by-city-over-time")
def get_sales_by_city_over_time():
    if not data_processor:
        raise HTTPException(status_code=404, detail="No data available. Please upload a file first.")
    return data_processor.calculate_sales_by_city_over_time()

@app.get("/")
def read_root():
    return {"Hello": "World"}

import pandas as pd

class DataProcessor:
    def __init__(self, file_stream):
        self.dataframes = self._load_and_validate_excel(file_stream)
        self.sales_history = self.dataframes['Fact_WeeklySales']
        self.latest_sales = self.sales_history.loc[self.sales_history.groupby(['tour_id', 'venue_id', 'event_date'])['verkaufswoche'].idxmax()]

    def _load_and_validate_excel(self, file_stream):
        # Simplified loader, as the data is now pre-processed
        try:
            return pd.read_excel(file_stream, sheet_name=None)
        except Exception as e:
            raise ValueError(f"Error processing Excel file: {e}")

    def calculate_kpis(self):
        total_units_sold = self.latest_sales['units_sold'].sum()
        sales_with_capacity = pd.merge(self.latest_sales, self.dataframes['Dim_Venue'], on='venue_id')
        total_capacity_sold_for_events = sales_with_capacity['capacity'].sum()
        overall_pct_capacity_sold = (total_units_sold / total_capacity_sold_for_events) * 100 if total_capacity_sold_for_events > 0 else 0
        num_active_tours = self.dataframes['Dim_Tour']['tour_id'].nunique()
        return {
            "total_units_sold": int(total_units_sold),
            "overall_pct_capacity_sold": round(overall_pct_capacity_sold, 2),
            "num_active_tours": int(num_active_tours)
        }

    def calculate_sales_by_artist(self):
        tours_with_artists = pd.merge(self.dataframes['Dim_Tour'], self.dataframes['Dim_Artist'], on='artist_id')
        sales_with_artists = pd.merge(self.latest_sales, tours_with_artists, on='tour_id')
        sales_by_artist = sales_with_artists.groupby('artist_name')['units_sold'].sum().reset_index()
        return sales_by_artist.to_dict(orient='records')

    def calculate_sales_over_time(self):
        sales_over_time = self.sales_history.groupby('verkaufswoche')['units_sold'].sum().reset_index()
        return sales_over_time.to_dict(orient='records')

    def calculate_sales_by_city_over_time(self):
        sales_with_details = pd.merge(self.sales_history, self.dataframes['Dim_Venue'], on='venue_id')
        sales_with_details = pd.merge(sales_with_details, self.dataframes['Dim_City'], on='city_id')
        pivot_table = sales_with_details.pivot_table(
            index='verkaufswoche',
            columns='city_name',
            values='units_sold',
            aggfunc='sum'
        ).fillna(0)
        pivot_table_records = pivot_table.reset_index().to_dict(orient='records')
        return pivot_table_records

