import { create } from 'zustand';
import { KPIResponse, SalesByArtist, SalesOverTime, SalesByCityOverTime } from '@/types/api';

interface DashboardStore {
  // Data states
  kpis: KPIResponse | null;
  salesByArtist: SalesByArtist[] | null;
  salesOverTime: SalesOverTime[] | null;
  salesByCityOverTime: SalesByCityOverTime[] | null;

  // Loading states
  isLoading: boolean;
  isUploading: boolean;

  // Error state
  error: string | null;

  // File upload state
  uploadedFile: File | null;
  uploadProgress: number;

  // Actions
  setKPIs: (kpis: KPIResponse) => void;
  setSalesByArtist: (data: SalesByArtist[]) => void;
  setSalesOverTime: (data: SalesOverTime[]) => void;
  setSalesByCityOverTime: (data: SalesByCityOverTime[]) => void;
  setLoading: (loading: boolean) => void;
  setUploading: (uploading: boolean) => void;
  setError: (error: string | null) => void;
  setUploadedFile: (file: File | null) => void;
  setUploadProgress: (progress: number) => void;
  clearData: () => void;
}

export const useStore = create<DashboardStore>((set) => ({
  // Initial state
  kpis: null,
  salesByArtist: null,
  salesOverTime: null,
  salesByCityOverTime: null,
  isLoading: false,
  isUploading: false,
  error: null,
  uploadedFile: null,
  uploadProgress: 0,

  // Actions
  setKPIs: (kpis) => set({ kpis }),
  setSalesByArtist: (data) => set({ salesByArtist: data }),
  setSalesOverTime: (data) => set({ salesOverTime: data }),
  setSalesByCityOverTime: (data) => set({ salesByCityOverTime: data }),
  setLoading: (loading) => set({ isLoading: loading }),
  setUploading: (uploading) => set({ isUploading: uploading }),
  setError: (error) => set({ error }),
  setUploadedFile: (file) => set({ uploadedFile: file }),
  setUploadProgress: (progress) => set({ uploadProgress: progress }),
  clearData: () => set({
    kpis: null,
    salesByArtist: null,
    salesOverTime: null,
    salesByCityOverTime: null,
    error: null,
    uploadedFile: null,
    uploadProgress: 0,
  }),
}));

