import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { BarChart3, Users, TrendingUp, Percent } from 'lucide-react';
import { KPICard } from '@/components/dashboard/KPICard';
import { useStore } from '@/stores/useStore';
import {
  fetchKPIs,
  fetchSalesByArtist,
  fetchSalesOverTime,
  fetchSalesByCityOverTime,
} from '@/services/api';

export const DashboardPage: React.FC = () => {
  const navigate = useNavigate();
  const {
    kpis,
    salesByArtist,
    salesOverTime,
    salesByCityOverTime,
    isLoading,
    error,
    uploadedFile,
    setKPIs,
    setSalesByArtist,
    setSalesOverTime,
    setSalesByCityOverTime,
    setLoading,
    setError,
  } = useStore();

  useEffect(() => {
    if (!uploadedFile) {
      navigate('/upload');
      return;
    }

    const loadData = async () => {
      setLoading(true);
      setError(null);

      try {
        const [kpisData, artistData, timeData, cityTimeData] = await Promise.all([
          fetchKPIs(),
          fetchSalesByArtist(),
          fetchSalesOverTime(),
          fetchSalesByCityOverTime(),
        ]);

        setKPIs(kpisData);
        setSalesByArtist(artistData);
        setSalesOverTime(timeData);
        setSalesByCityOverTime(cityTimeData);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to load data');
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, [
    uploadedFile,
    navigate,
    setKPIs,
    setSalesByArtist,
    setSalesOverTime,
    setSalesByCityOverTime,
    setLoading,
    setError,
  ]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading dashboard data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <p className="text-red-600 mb-4">{error}</p>
          <button
            onClick={() => navigate('/upload')}
            className="text-blue-600 hover:underline"
          >
            Go back to upload
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <BarChart3 className="w-8 h-8 text-blue-600" />
              <h1 className="text-2xl font-bold text-gray-900">Sales Dashboard</h1>
            </div>
            <button
              onClick={() => navigate('/upload')}
              className="text-sm text-blue-600 hover:underline"
            >
              Upload New Data
            </button>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <KPICard
            title="Total Units Sold"
            value={kpis?.total_units_sold || 0}
            icon={<BarChart3 className="w-5 h-5" />}
            format="number"
          />
          <KPICard
            title="Capacity Utilization"
            value={kpis?.overall_pct_capacity_sold || 0}
            icon={<Percent className="w-5 h-5" />}
            format="percent"
            subtitle="Overall venue capacity sold"
          />
          <KPICard
            title="Active Tours"
            value={kpis?.num_active_tours || 0}
            icon={<Users className="w-5 h-5" />}
            format="number"
          />
        </div>

        <div className="mt-8 p-6 bg-white rounded-lg shadow">
          <h2 className="text-lg font-semibold text-gray-900 mb-4">
            Sales Analytics Charts
          </h2>
          <p className="text-gray-600">
            Chart visualizations will be implemented in the next phase.
          </p>
          <div className="mt-4 space-y-2 text-sm text-gray-500">
            <p>‚Ä¢ Sales by Artist: {salesByArtist?.length || 0} artists</p>
            <p>‚Ä¢ Sales Over Time: {salesOverTime?.length || 0} weeks of data</p>
            <p>‚Ä¢ Sales by City: {salesByCityOverTime?.length || 0} time periods</p>
          </div>
        </div>
      </main>
    </div>
  );
};

