import React from 'react';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  Cell,
} from 'recharts';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { SalesByArtist } from '@/types/api';

interface SalesByArtistChartProps {
  data: SalesByArtist[];
}

const COLORS = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6'];

export const SalesByArtistChart: React.FC<SalesByArtistChartProps> = ({ data }) => {
  const sortedData = [...data].sort((a, b) => b.units_sold - a.units_sold);

  return (
    <Card>
      <CardHeader>
        <CardTitle>Sales by Artist</CardTitle>
      </CardHeader>
      <CardContent>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart
            data={sortedData}
            layout="horizontal"
            margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="artist_name" />
            <YAxis />
            <Tooltip
              formatter={(value: number) => [value.toLocaleString(), 'Units Sold']}
            />
            <Bar dataKey="units_sold" radius={[4, 4, 0, 0]}>
              {sortedData.map((_, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Bar>
          </BarChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
};

