import axios from 'axios';
import { KPIResponse, SalesByArtist, SalesOverTime, SalesByCityOverTime } from '@/types/api';

const api = axios.create({
  baseURL: '',
  headers: {
    'Content-Type': 'application/json',
  },
});

export const uploadFile = async (file: File, onProgress?: (progress: number) => void) => {
  const formData = new FormData();
  formData.append('file', file);

  const response = await api.post('/upload/', formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
    onUploadProgress: (progressEvent) => {
      if (progressEvent.total && onProgress) {
        const progress = Math.round((progressEvent.loaded * 100) / progressEvent.total);
        onProgress(progress);
      }
    },
  });

  return response.data;
};

export const fetchKPIs = async (): Promise<KPIResponse> => {
  const response = await api.get<KPIResponse>('/api/kpis');
  return response.data;
};

export const fetchSalesByArtist = async (): Promise<SalesByArtist[]> => {
  const response = await api.get<SalesByArtist[]>('/api/sales-by-artist');
  return response.data;
};

export const fetchSalesOverTime = async (): Promise<SalesOverTime[]> => {
  const response = await api.get<SalesOverTime[]>('/api/sales-over-time');
  return response.data;
};

export const fetchSalesByCityOverTime = async (): Promise<SalesByCityOverTime[]> => {
  const response = await api.get<SalesByCityOverTime[]>('/api/sales-by-city-over-time');
  return response.data;
};

