  const response = await api.get<SalesOverTime[]>('/api/sales-over-time');
  return response.data;
};

export const fetchSalesByCityOverTime = async (): Promise<SalesByCityOverTime[]> => {
  const response = await api.get<SalesByCityOverTime[]>('/api/sales-by-city-over-time');
  return response.data;
};

