from fastapi import FastAPI, HTTPException, Query
from fastapi.middleware.cors import CORSMiddleware
from data_processor_v2 import DataProcessorV2
from typing import Optional
import io
import os
from pathlib import Path

app = FastAPI()

# Configure CORS
app.add_middleware(
    CORSMiddleware,
    allow_origins=["http://localhost:3000", "http://localhost:3001", "http://localhost:3002", "http://localhost:3003"],
    allow_credentials=True,
    allow_methods=["*"],
    allow_headers=["*"],
)

# Global data processor
data_processor = None
original_csv_files = None
dataset_status = {
    "data_loaded": False,
    "data_source": "csv_files",
    "csv_files_found": [],
    "missing_files": [],
    "load_error": None,
    "artists_count": 0
}

def check_csv_dataset():
    """Check if complete CSV dataset exists"""
    global dataset_status

    # Define the folder path (relative to backend directory)
    folder_path = Path("../../VVK_Zahlen")
    required_files = ['artists', 'venues', 'tours', 'concerts', 'weekly_sales']

    dataset_status["csv_files_found"] = []
    dataset_status["missing_files"] = []

    for filename in required_files:
        file_path = folder_path / f"{filename}.csv"
        if file_path.exists():
            dataset_status["csv_files_found"].append(f"{filename}.csv")
        else:
            dataset_status["missing_files"].append(f"{filename}.csv")

    # Dataset is complete if all required files are found
    return len(dataset_status["missing_files"]) == 0

def load_csv_data():
    """Load CSV data on startup"""
    global data_processor, original_csv_files, dataset_status

    try:
        print("ğŸ” Checking for CSV dataset...")

        if not check_csv_dataset():
            missing_files_str = ", ".join(dataset_status["missing_files"])
            error_msg = f"Incomplete dataset. Missing files: {missing_files_str}"
            print(f"âŒ {error_msg}")
            dataset_status["load_error"] = error_msg
            return False

        print("âœ… Complete CSV dataset found!")
        print("ğŸš€ Loading CSV data...")

        # Define the folder path (relative to backend directory)
        folder_path = "../../VVK_Zahlen"

        # Load CSV files from folder
        csv_files = {}
        required_files = ['artists', 'venues', 'tours', 'concerts', 'weekly_sales']

        for filename in required_files:
            file_path = os.path.join(folder_path, f"{filename}.csv")

            with open(file_path, 'rb') as f:
                csv_files[filename] = io.BytesIO(f.read())
            print(f"âœ… Loaded {filename}.csv")

        # Store original files for artist filtering
        original_csv_files = csv_files

        # Create data processor without artist filter
        data_processor = DataProcessorV2(csv_files)

        artists_count = len(data_processor.get_available_artists())
        dataset_status["data_loaded"] = True
        dataset_status["artists_count"] = artists_count
        dataset_status["load_error"] = None

        print(f"ğŸ‰ Successfully loaded {artists_count} artists from CSV data!")
        print("ğŸ“Š Dashboard ready to serve data!")

        return True

    except Exception as e:
        error_msg = f"Error loading CSV data: {str(e)}"
        print(f"âŒ {error_msg}")
        dataset_status["load_error"] = error_msg
        dataset_status["data_loaded"] = False
        import traceback
        traceback.print_exc()
        return False

# Load data on startup
print("ğŸ Starting Sales Dashboard API v2.0...")
if not load_csv_data():
    print("âš ï¸  Failed to load CSV data on startup. Dashboard will run in limited mode.")
    print("ğŸ’¡ Place complete CSV dataset in VVK_Zahlen/ directory and restart the server.")

@app.get("/")
def read_root():
    return {
        "message": "Sales Dashboard API v2.0 - CSV Database Mode",
        "version": "2.0.0",
        "data_source": dataset_status["data_source"],
        "dataset_status": dataset_status,
        "ready": dataset_status["data_loaded"],
        "endpoints": {
            "status": "GET /api/status",
            "artists": "GET /api/artists",
            "kpis": "GET /api/kpis?artist=<artist_name>",
            "sales_over_time": "GET /api/sales-over-time?artist=<artist_name>",
            "sales_trends": "GET /api/sales-trends?artist=<artist_name>",
            "sales_by_city_over_time": "GET /api/sales-by-city-over-time?artist=<artist_name>",
            "reload_data": "POST /reload-data",
            "docs": "GET /docs"
        }
    }

@app.get("/api/status")
def get_status():
    """Get detailed status about data availability"""
    return {
        "ready": dataset_status["data_loaded"],
        "data_source": dataset_status["data_source"],
        "dataset_info": {
            "csv_files_found": dataset_status["csv_files_found"],
            "missing_files": dataset_status["missing_files"],
            "artists_count": dataset_status["artists_count"]
        },
        "error": dataset_status["load_error"]
    }

@app.post("/reload-data/")
async def reload_data():
    """Reload CSV data"""
    if load_csv_data():
        return {
            "message": "CSV data reloaded successfully",
            "artists_loaded": len(data_processor.get_available_artists()) if data_processor else 0
        }
    else:
        raise HTTPException(status_code=500, detail="Failed to reload CSV data")

def get_filtered_processor(artist: str):
    """Helper function to create a filtered processor for a specific artist."""
    if not original_csv_files:
        raise HTTPException(status_code=404, detail="No data available. CSV data not loaded.")

    # Create new BytesIO objects from the original data
    csv_files_copy = {}
    for name, original_stream in original_csv_files.items():
        original_stream.seek(0)  # Reset to beginning
        csv_files_copy[name] = io.BytesIO(original_stream.read())

    return DataProcessorV2(csv_files_copy, artist_filter=artist)

@app.get("/api/artists")
def get_artists():
    if not data_processor:
        raise HTTPException(status_code=404, detail="No data available. CSV data not loaded.")
    return {"artists": data_processor.get_available_artists()}

@app.get("/api/kpis")
def get_kpis(artist: Optional[str] = Query(None, description="Filter by artist name")):
    if not data_processor:
        raise HTTPException(status_code=404, detail="No data available. CSV data not loaded.")

    if artist:
        try:
            filtered_processor = get_filtered_processor(artist)
            return filtered_processor.calculate_kpis()
        except ValueError as e:
            raise HTTPException(status_code=400, detail=str(e))
    else:
        return data_processor.calculate_kpis()

@app.get("/api/sales-over-time")
def get_sales_over_time(artist: Optional[str] = Query(None, description="Filter by artist name")):
    if not data_processor:
        raise HTTPException(status_code=404, detail="No data available. CSV data not loaded.")

    if artist:
        try:
            filtered_processor = get_filtered_processor(artist)
            return filtered_processor.calculate_sales_over_time()
        except ValueError as e:
            raise HTTPException(status_code=400, detail=str(e))
    else:
        return data_processor.calculate_sales_over_time()

@app.get("/api/sales-trends")
def get_sales_trends(artist: Optional[str] = Query(None, description="Filter by artist name")):
    if not data_processor:
        raise HTTPException(status_code=404, detail="No data available. CSV data not loaded.")

    if artist:
        try:
            filtered_processor = get_filtered_processor(artist)
            return filtered_processor.calculate_sales_trends()
        except ValueError as e:
            raise HTTPException(status_code=400, detail=str(e))
    else:
        return data_processor.calculate_sales_trends()

@app.get("/api/sales-by-city-over-time")
def get_sales_by_city_over_time(artist: Optional[str] = Query(None, description="Filter by artist name")):
    if not data_processor:
        raise HTTPException(status_code=404, detail="No data available. CSV data not loaded.")

    if artist:
        try:
            filtered_processor = get_filtered_processor(artist)
            return filtered_processor.calculate_sales_by_city_over_time()
        except ValueError as e:
            raise HTTPException(status_code=400, detail=str(e))
    else:
        return data_processor.calculate_sales_by_city_over_time()

if __name__ == "__main__":
    import uvicorn
    uvicorn.run(app, host="0.0.0.0", port=8083)

