import React, { useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { BarChart3, Users, Percent } from 'lucide-react';
import { KPICard } from '@/components/dashboard/KPICard';
import { SalesTrendsCard } from '@/components/dashboard/SalesTrendsCard';
import { ArtistTabs } from '@/components/navigation/ArtistTabs';
import { SalesOverTimeChart } from '@/components/charts/SalesOverTimeChart';
import { SalesByCityOverTimeChart } from '@/components/charts/SalesByCityOverTimeChart';
import { useStore } from '@/stores/useStore';
import {
  fetchKPIs,
  fetchSalesOverTime,
  fetchSalesByCityOverTime,
  fetchSalesTrends,
  fetchArtists,
  checkStatus,
} from '@/services/api';

export const DashboardPage: React.FC = () => {
  const navigate = useNavigate();
  const {
    // Artist state
    selectedArtist,
    setAvailableArtists,

    // Data state
    kpis,
    salesOverTime,
    salesByCityOverTime,
    salesTrends,
    isLoading,
    error,

    // Actions
    setKPIs,
    setSalesOverTime,
    setSalesByCityOverTime,
    setSalesTrends,
    setLoading,
    setError,
  } = useStore();

  const loadDashboardData = useCallback(async (artist?: string | null) => {
    setLoading(true);
    setError(null);

    try {
      const [kpisData, timeData, cityTimeData, trendsData] = await Promise.all([
        fetchKPIs(artist || undefined),
        fetchSalesOverTime(artist || undefined),
        fetchSalesByCityOverTime(artist || undefined),
        fetchSalesTrends(artist || undefined),
      ]);

      setKPIs(kpisData);
      setSalesOverTime(timeData);
      setSalesByCityOverTime(cityTimeData);
      setSalesTrends(trendsData);
    } catch (err) {
      console.error('Error loading dashboard data:', err);
      setError('Failed to load dashboard data. Please try again.');
    } finally {
      setLoading(false);
    }
  }, [setKPIs, setSalesOverTime, setSalesByCityOverTime, setSalesTrends, setLoading, setError]);

  const loadArtists = useCallback(async () => {
    try {
      const artistsData = await fetchArtists();
      setAvailableArtists(artistsData.artists);
    } catch (err) {
      console.error('Error loading artists:', err);
      setError('Failed to load artists data.');
    }
  }, [setAvailableArtists, setError]);

  useEffect(() => {
    const initializeDashboard = async () => {
      try {
        // Check if backend has data available
        const status = await checkStatus();

        if (!status.ready) {
          console.log('No data available, redirecting to upload page');
          navigate('/upload');
          return;
        }

        // Load artists first, then load dashboard data
        await loadArtists();
        await loadDashboardData(selectedArtist);
      } catch (err) {
        console.error('Error checking status:', err);
        setError('Failed to connect to backend. Please check if the server is running.');
      }
    };

    initializeDashboard();
  }, [navigate, loadArtists, loadDashboardData, selectedArtist, setError]);

  const handleArtistChange = useCallback((artist: string | null) => {
    loadDashboardData(artist);
  }, [loadDashboardData]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading dashboard data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="text-red-600 mb-4">
            <BarChart3 className="w-12 h-12 mx-auto mb-2" />
            <p className="text-lg font-semibold">Error Loading Dashboard</p>
          </div>
          <p className="text-gray-600 mb-4">{error}</p>
          <div className="space-x-2">
            <button
              onClick={() => window.location.reload()}
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
            >
              Retry
            </button>
            <button
              onClick={() => navigate('/upload')}
              className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700"
            >
              Check Status
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-4">
              <BarChart3 className="w-8 h-8 text-blue-600" />
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Sales Dashboard</h1>
                <p className="text-sm text-gray-600">
                  {selectedArtist ? `Showing data for ${selectedArtist}` : 'Showing data for all artists'}
                </p>
              </div>
            </div>
            <button
              onClick={() => navigate('/upload')}
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
            >
              Check Status
            </button>
          </div>
        </div>
      </header>

      {/* Artist Navigation Tabs */}
      <ArtistTabs onArtistChange={handleArtistChange} />

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* KPI Cards */}
        <div className={`grid grid-cols-1 gap-6 mb-8 ${kpis?.overall_pct_capacity_sold !== undefined ? 'md:grid-cols-3' : 'md:grid-cols-2'}`}>
          <KPICard
            title="Total Units Sold"
            value={kpis?.total_units_sold || 0}
            icon={<BarChart3 className="w-6 h-6" />}
            format="number"
          />
          {kpis?.overall_pct_capacity_sold !== undefined ? (
            <KPICard
              title="Capacity Utilization"
              value={kpis.overall_pct_capacity_sold}
              icon={<Percent className="w-6 h-6" />}
              format="percent"
            />
          ) : (
            <KPICard
              title="Capacity Data"
              value="Not Available"
              subtitle={kpis?.capacity_message || "No venue capacity data in dataset"}
              icon={<Percent className="w-6 h-6" />}
            />
          )}
          <KPICard
            title="Active Tours"
            value={kpis?.num_active_tours || 0}
            icon={<Users className="w-6 h-6" />}
            format="number"
          />
        </div>

        {/* Charts */}
        <div className="space-y-8">
          {salesTrends && (
            <SalesTrendsCard trends={salesTrends} />
          )}

          {salesOverTime && salesOverTime.length > 0 && (
            <SalesOverTimeChart data={salesOverTime} />
          )}

          {salesByCityOverTime && salesByCityOverTime.length > 0 && (
            <SalesByCityOverTimeChart data={salesByCityOverTime} />
          )}
        </div>
      </main>
    </div>
  );
};

