import axios from 'axios';
import { KPIResponse, SalesOverTime, SalesByCityOverTime, SalesTrends, ArtistListResponse } from '@/types/api';

// Use relative URLs to leverage Vite's proxy configuration
const API_BASE_URL = '';

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
});

export interface StatusResponse {
  ready: boolean;
  data_source: string;
  dataset_info: {
    csv_files_found: string[];
    missing_files: string[];
    artists_count: number;
  };
  error: string | null;
}

export const checkStatus = async (): Promise<StatusResponse> => {
  const response = await api.get('/api/status');
  return response.data;
};

export const uploadFile = async (file: File): Promise<{message: string; filename: string}> => {
  const formData = new FormData();
  formData.append('file', file);

  const response = await api.post('/upload/', formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  });

  return response.data;
};

export const fetchArtists = async (): Promise<ArtistListResponse> => {
  const response = await api.get<ArtistListResponse>('/api/artists');
  return response.data;
};

export const fetchKPIs = async (artist?: string): Promise<KPIResponse> => {
  const params = artist ? { artist } : {};
  const response = await api.get<KPIResponse>('/api/kpis', { params });
  return response.data;
};

export const fetchSalesOverTime = async (artist?: string): Promise<SalesOverTime[]> => {
  const params = artist ? { artist } : {};
  const response = await api.get<SalesOverTime[]>('/api/sales-over-time', { params });
  return response.data;
};

export const fetchSalesTrends = async (artist?: string): Promise<SalesTrends> => {
  const params = artist ? { artist } : {};
  const response = await api.get<SalesTrends>('/api/sales-trends', { params });
  return response.data;
};

export const fetchSalesByCityOverTime = async (artist?: string): Promise<SalesByCityOverTime[]> => {
  const params = artist ? { artist } : {};
  const response = await api.get<SalesByCityOverTime[]>('/api/sales-by-city-over-time', { params });
  return response.data;
};

import { create } from 'zustand';
import { persist } from 'zustand/middleware';
import { KPIResponse, SalesOverTime, SalesByCityOverTime, SalesTrends } from '@/types/api';

interface StoreState {
  // Artist management
  availableArtists: string[];
  selectedArtist: string | null;

  // Data states
  kpis: KPIResponse | null;
  salesOverTime: SalesOverTime[] | null;
  salesByCityOverTime: SalesByCityOverTime[] | null;
  salesTrends: SalesTrends | null;

  // Loading states
  isLoading: boolean;

  // Error state
  error: string | null;

  // Artist actions
  setAvailableArtists: (artists: string[]) => void;
  setSelectedArtist: (artist: string | null) => void;

  // Data actions
  setKPIs: (kpis: KPIResponse) => void;
  setSalesOverTime: (data: SalesOverTime[]) => void;
  setSalesByCityOverTime: (data: SalesByCityOverTime[]) => void;
  setSalesTrends: (data: SalesTrends) => void;

  // UI actions
  setLoading: (loading: boolean) => void;
  setError: (error: string | null) => void;
  clearError: () => void;

  // Clear all data
  clearData: () => void;
}

export const useStore = create<StoreState>()(
  persist(
    (set) => ({
      // Artist management
      availableArtists: [],
      selectedArtist: null,

      // Data states
      kpis: null,
      salesOverTime: null,
      salesByCityOverTime: null,
      salesTrends: null,

      // Loading states
      isLoading: false,

      // Error state
      error: null,

      // Artist actions
      setAvailableArtists: (artists) => set({ availableArtists: artists }),
      setSelectedArtist: (artist) => set({ selectedArtist: artist }),

      // Data actions
      setKPIs: (kpis) => set({ kpis }),
      setSalesOverTime: (data) => set({ salesOverTime: data }),
      setSalesByCityOverTime: (data) => set({ salesByCityOverTime: data }),
      setSalesTrends: (data) => set({ salesTrends: data }),

      // UI actions
      setLoading: (loading) => set({ isLoading: loading }),
      setError: (error) => set({ error }),
      clearError: () => set({ error: null }),

      // Clear all data
      clearData: () => set({
        kpis: null,
        salesOverTime: null,
        salesByCityOverTime: null,
        salesTrends: null,
        error: null,
        availableArtists: [],
        selectedArtist: null,
      }),
    }),
    {
      name: 'sales-dashboard-store',
      partialize: (state) => ({
        availableArtists: state.availableArtists,
        selectedArtist: state.selectedArtist,
      }),
    }
  )
);

import React from 'react';
import {
  ResponsiveContainer,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Area,
  AreaChart,
} from 'recharts';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { SalesOverTime } from '@/types/api';

interface SalesOverTimeChartProps {
  data: SalesOverTime[];
}

export const SalesOverTimeChart: React.FC<SalesOverTimeChartProps> = ({ data }) => {
  const formatWeek = (week: number) => `Week ${week}`;

  return (
    <Card>
      <CardHeader>
        <CardTitle>Sales Over Time</CardTitle>
      </CardHeader>
      <CardContent>
        <ResponsiveContainer width="100%" height={300}>
          <AreaChart
            data={data}
            margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
          >
            <defs>
              <linearGradient id="colorSales" x1="0" y1="0" x2="0" y2="1">
                <stop offset="5%" stopColor="#3B82F6" stopOpacity={0.8} />
                <stop offset="95%" stopColor="#3B82F6" stopOpacity={0} />
              </linearGradient>
            </defs>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis
              dataKey="week"
              tickFormatter={(week) => `${week}`}
            />
            <YAxis />
            <Tooltip
              labelFormatter={(label) => `Week ${label}`}
              formatter={(value: number) => [value.toLocaleString(), 'Units Sold']}
            />
            <Area
              type="monotone"
              dataKey="units_sold"
              stroke="#3B82F6"
              fillOpacity={1}
              fill="url(#colorSales)"
              strokeWidth={2}
            />
          </AreaChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
};

