export interface Artist {
  name: string;
}

export interface ArtistListResponse {
  artists: string[];
}

export interface KPIResponse {
  total_units_sold: number;
  overall_pct_capacity_sold?: number;
  num_active_tours: number;
  capacity_data_available?: boolean;
  capacity_message?: string;
}

export interface SalesOverTime {
  verkaufswoche: number;
  units_sold: number;
}

export interface SalesByCityOverTime {
  verkaufswoche: number;
  [cityName: string]: number;
}

export interface City {
  city_id: number;
  city_name: string;
}

export interface Venue {
  venue_id: number;
  city_id: number;
  venue_name: string;
  capacity: number;
}

export interface WeeklySales {
  tour_id: number;
  venue_id: number;
  event_date: string;
  verkaufswoche: number;
  units_sold: number;
  sales_id: number;
}

export interface SalesTrends {
  current_week: {
    week: number;
    sales: number;
    change_from_previous: number;
    pct_change_from_previous: number;
  };
  previous_week: {
    week: number;
    sales: number;
  };
  trend_analysis: {
    direction: 'up' | 'down' | 'stable';
    strength: 'strong' | 'moderate' | 'weak';
    avg_weekly_sales: number;
    peak_week: {
      week: number;
      sales: number;
    };
    lowest_week: {
      week: number;
      sales: number;
    };
  };
  weekly_progression: {
    verkaufswoche: number;
    units_sold: number;
    week_over_week_change: number;
    week_over_week_pct_change: number;
    ma_4_week: number;
  }[];
}

export interface ApiError {
  detail: string;
}

