awk -F';' 'NR>1 {weeks[$3] += $5} END {for (week in weeks) print week":"weeks[week]}' sdp_sales.csv | sort -V | awk -F':' 'NR>1 {
  week = substr($1, 4);
  sales = $2;
  if (prev_sales > 0) {
    change = ((sales - prev_sales) / prev_sales) * 100;
    printf "Week %s to %s: %+.1f%% (%d -> %d tickets)\n", prev_week, week, change, prev_sales, sales;
  }
  prev_week = week;
  prev_sales = sales;
}'

