import React from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { SalesByCityOverTime } from '@/types/api';

interface SalesByCityOverTimeChartProps {
  data: SalesByCityOverTime[];
}

const COLORS = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6', '#EC4899', '#14B8A6'];

export const SalesByCityOverTimeChart: React.FC<SalesByCityOverTimeChartProps> = ({ data }) => {
  if (!data || data.length === 0) return null;

  // Extract city names (all keys except verkaufswoche)
  const cities = Object.keys(data[0]).filter(key => key !== 'verkaufswoche');

  return (
    <Card>
      <CardHeader>
        <CardTitle>Sales by City Over Time</CardTitle>
      </CardHeader>
      <CardContent>
        <ResponsiveContainer width="100%" height={400}>
          <LineChart
            data={data}
            margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis
              dataKey="week"
              tickFormatter={(week) => `${week}`}
            />
            <YAxis />
            <Tooltip
              labelFormatter={(label) => `Week ${label}`}
              formatter={(value: number) => [value.toLocaleString(), 'Units']}
            />
            <Legend />
            {cities.map((city, index) => (
              <Line
                key={city}
                type="monotone"
                dataKey={city}
                stroke={COLORS[index % COLORS.length]}
                strokeWidth={2}
                dot={{ fill: COLORS[index % COLORS.length], r: 4 }}
                activeDot={{ r: 6 }}
              />
            ))}
          </LineChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
};

