import React, { useState, useCallback } from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { SalesByCityOverTime } from '@/types/api';

interface SalesByCityOverTimeChartProps {
  data: SalesByCityOverTime[];
}

const COLORS = ['#3B82F6', '#10B981', '#F59E0B', '#EF4444', '#8B5CF6', '#EC4899', '#14B8A6'];

export const SalesByCityOverTimeChart: React.FC<SalesByCityOverTimeChartProps> = ({ data }) => {
  if (!data || data.length === 0) return null;

  // Extract city names (all keys except week)
  const cities = Object.keys(data[0]).filter(key => key !== 'week');

  // State to track visible cities
  const [visibleCities, setVisibleCities] = useState<Set<string>>(new Set(cities));

  // Handler to clear all cities
  const handleClearCities = useCallback(() => {
    setVisibleCities(new Set());
  }, []);

  // Handler to toggle city visibility
  const handleCityClick = useCallback((city: string) => {
    setVisibleCities(prev => {
      const newSet = new Set(prev);
      if (newSet.has(city)) {
        newSet.delete(city);
      } else {
        newSet.add(city);
      }
      return newSet;
    });
  }, []);

  // Custom legend component with click functionality
  const CustomLegend = (props: any) => {
    const { payload } = props;
    return (
      <ul style={{ display: 'flex', flexWrap: 'wrap', padding: 0, margin: 0, justifyContent: 'center' }}>
        {payload.map((entry: any, index: number) => (
          <li
            key={`item-${index}`}
            style={{
              display: 'inline-flex',
              alignItems: 'center',
              marginRight: 20,
              cursor: 'pointer',
              opacity: visibleCities.has(entry.value) ? 1 : 0.3,
            }}
            onClick={() => handleCityClick(entry.value)}
          >
            <span
              style={{
                display: 'inline-block',
                width: 12,
                height: 12,
                backgroundColor: entry.color,
                marginRight: 5,
                borderRadius: '50%',
              }}
            />
            <span style={{ color: visibleCities.has(entry.value) ? '#000' : '#999' }}>
              {entry.value}
            </span>
          </li>
        ))}
      </ul>
    );
  };

  return (
    <Card>
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle>Sales by City Over Time</CardTitle>
        <Button
          variant="outline"
          size="sm"
          onClick={handleClearCities}
          className="ml-auto"
        >
          Clear Cities
        </Button>
      </CardHeader>
      <CardContent>
        <ResponsiveContainer width="100%" height={400}>
          <LineChart
            data={data}
            margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis
              dataKey="week"
              tickFormatter={(week) => `${week}`}
            />
            <YAxis />
            <Tooltip
              labelFormatter={(label) => `Week ${label}`}
              formatter={(value: number) => [value.toLocaleString(), 'Units']}
            />
            <Legend />
            {cities.map((city, index) => (
              <Line
                key={city}
                type="monotone"
                dataKey={city}
                stroke={COLORS[index % COLORS.length]}
                strokeWidth={2}
                dot={{ fill: COLORS[index % COLORS.length], r: 4 }}
                activeDot={{ r: 6 }}
              />
            ))}
          </LineChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
};

