import React from 'react';
import { TrendingUp, TrendingDown, Minus, ArrowUp, ArrowDown } from 'lucide-react';
import { SalesTrends } from '@/types/api';

interface SalesTrendsCardProps {
  trends: SalesTrends | null;
}

export const SalesTrendsCard: React.FC<SalesTrendsCardProps> = ({ trends }) => {
  if (!trends || !trends.weekly_progression || trends.weekly_progression.length === 0) {
    return (
        <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-900">Sales Trends</h3>
            <p className="text-center text-gray-500 mt-4">No trend data available.</p>
        </div>
    );
  }

  const { current_week, previous_week, trend_analysis, weekly_progression } = trends;

  // Get trend icon and color based on direction
  const getTrendIcon = (direction: string, strength: string) => {
    const iconClass = strength === 'strong' ? 'w-6 h-6' : 'w-5 h-5';

    switch (direction) {
      case 'increasing':
        return <TrendingUp className={`${iconClass} text-green-500`} />;
      case 'decreasing':
        return <TrendingDown className={`${iconClass} text-red-500`} />;
      default:
        return <Minus className={`${iconClass} text-gray-500`} />;
    }
  };

  // Get change indicator
  const getChangeIndicator = (change: number, pctChange: number) => {
    if (change > 0) {
      return (
        <div className="flex items-center space-x-1 text-green-600">
          <ArrowUp className="w-4 h-4" />
          <span className="text-sm font-medium">+{change.toLocaleString()}</span>
          <span className="text-xs">({pctChange > 0 ? '+' : ''}{pctChange.toFixed(1)}%)</span>
        </div>
      );
    } else if (change < 0) {
      return (
        <div className="flex items-center space-x-1 text-red-600">
          <ArrowDown className="w-4 h-4" />
          <span className="text-sm font-medium">{change.toLocaleString()}</span>
          <span className="text-xs">({pctChange.toFixed(1)}%)</span>
        </div>
      );
    } else {
      return (
        <div className="flex items-center space-x-1 text-gray-500">
          <Minus className="w-4 h-4" />
          <span className="text-sm font-medium">No change</span>
        </div>
      );
    }
  };

  // Get trend strength color
  const getTrendStrengthColor = (strength: string) => {
    switch (strength) {
      case 'strong':
        return 'text-blue-600 bg-blue-100';
      case 'weak':
        return 'text-yellow-600 bg-yellow-100';
      default:
        return 'text-gray-600 bg-gray-100';
    }
  };

  // Create mini chart data for last 8 weeks
  const chartData = weekly_progression.slice(-8);
  const salesData = chartData.map(d => d.sales);
  const maxSales = Math.max(...salesData, 0);
  const minSales = Math.min(...salesData, 0);

  return (
    <div className="bg-white rounded-lg shadow p-6">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-lg font-semibold text-gray-900">Sales Trends</h3>
        <div className="flex items-center space-x-2">
          {getTrendIcon(trend_analysis.direction, trend_analysis.strength)}
          <span className={`px-2 py-1 rounded-full text-xs font-medium ${getTrendStrengthColor(trend_analysis.strength)}`}>
            {trend_analysis.strength} {trend_analysis.direction}
          </span>
        </div>
      </div>

      {/* Current Week Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
        <div className="bg-blue-50 rounded-lg p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-blue-600 font-medium">Current Week ({current_week.week})</p>
              <p className="text-2xl font-bold text-blue-900">{current_week.sales.toLocaleString()}</p>
            </div>
            <div className="text-right">
              <p className="text-xs text-blue-600 mb-1">vs Previous Week</p>
              {getChangeIndicator(current_week.change_from_previous, current_week.pct_change_from_previous)}
            </div>
          </div>
        </div>

        <div className="bg-gray-50 rounded-lg p-4">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600 font-medium">Previous Week ({previous_week.week})</p>
              <p className="text-2xl font-bold text-gray-900">{previous_week.sales.toLocaleString()}</p>
            </div>
            <div className="text-right">
              <p className="text-xs text-gray-600 mb-1">Weekly Average</p>
              <p className="text-sm font-medium text-gray-700">{Math.round(trend_analysis.avg_weekly_sales).toLocaleString()}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Mini Chart */}
      <div className="mb-6">
        <h4 className="text-sm font-medium text-gray-700 mb-3">Last 8 Weeks Trend</h4>
        <div className="flex items-end justify-between h-20 bg-gray-50 rounded-lg p-3">
          {chartData.map((week) => {
            const height = maxSales > minSales ? ((week.sales - minSales) / (maxSales - minSales)) * 100 : 50;
            const isCurrentWeek = week.week === current_week.week;

            return (
              <div key={week.week} className="flex flex-col items-center space-y-1">
                <div className="text-xs text-gray-500">{week.sales.toLocaleString()}</div>
                <div 
                  className={`w-6 rounded-t transition-all duration-300 ${
                    isCurrentWeek ? 'bg-blue-500' : 'bg-gray-400'
                  }`}
                  style={{ height: `${Math.max(height, 5)}%` }}
                />
                <div className={`text-xs ${isCurrentWeek ? 'font-bold text-blue-600' : 'text-gray-500'}`}>
                  W{week.week}
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Key Metrics */}
      <div className="grid grid-cols-2 gap-4 text-sm">
        <div className="bg-green-50 rounded-lg p-3">
          <p className="text-green-600 font-medium">Peak Week</p>
          <p className="text-green-800">Week {trend_analysis.peak_week.week}</p>
          <p className="text-green-900 font-semibold">{trend_analysis.peak_week.sales.toLocaleString()} sales</p>
        </div>
        <div className="bg-red-50 rounded-lg p-3">
          <p className="text-red-600 font-medium">Lowest Week</p>
          <p className="text-red-800">Week {trend_analysis.lowest_week.week}</p>
          <p className="text-red-900 font-semibold">{trend_analysis.lowest_week.sales.toLocaleString()} sales</p>
        </div>
      </div>

      {/* Moving Average Info Removed */}
      <div className="mt-4 pt-4 border-t border-gray-200" />
    </div>
  );
};

